SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_obter_se_conta_monitor ( nr_seq_conta_p pls_conta.nr_sequencia%type, ie_opcao_p text) AS $body$
DECLARE


/*
ie_opcao_p
C - Conta médica
R - Recurso de glosa
*/
qt_monitor_w	integer;


BEGIN

if (ie_opcao_p = 'C') then

	select	count(1)
	into STRICT	qt_monitor_w
	from	pls_monitor_tiss_alt_guia a,
		pls_monitor_tiss_alt b
	where	b.nr_sequencia = a.nr_seq_cta_alt
	and	b.nr_seq_conta = nr_seq_conta_p
	and	b.ie_tipo_evento not in ('FR','PR');

elsif (ie_opcao_p = 'R') then

	select	count(1)
	into STRICT	qt_monitor_w
	from	pls_monitor_tiss_alt_guia a,
		pls_monitor_tiss_alt b
	where	b.nr_sequencia = a.nr_seq_cta_alt
	and	b.nr_seq_conta = nr_seq_conta_p
	and	b.ie_tipo_evento in ('FR','PR');
end if;

if (qt_monitor_w > 0) then
	-- Não é possível estornar os valores de coparticipação da conta #@NR_SEQ_CONTA#@. Já existem registros na ANS para a mesma.
	CALL wheb_mensagem_pck.exibir_mensagem_abort(733702, 'NR_SEQ_CONTA=' || nr_seq_conta_p);
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
