CREATE OR REPLACE PROCEDURE pls_obter_se_conv_mat_unimed ( nm_atributo_p text, dt_referencia_p timestamp, ds_valor_atual_p text, ds_valor_novo_p text, ie_altera_valor_p INOUT text) AS $body$
DECLARE


ie_aplicar_conversao_w	varchar(10);
ie_altera_valor_w	varchar(10) := 'N';


BEGIN

select	max(ie_aplicar_conversao)
into STRICT	ie_aplicar_conversao_w
from	pls_regra_conv_mat_unimed
where	nm_atributo	= nm_atributo_p
and	dt_referencia_p between dt_inicio_vigencia and coalesce(dt_fim_vigencia,dt_referencia_p);

if (ie_aplicar_conversao_w = 'S') then
	if (upper(coalesce(ds_valor_atual_p,'X')) <> upper(coalesce(ds_valor_novo_p,'X'))) then
		ie_altera_valor_w	:= 'S';
	end if;
end if;

ie_altera_valor_p	:= ie_altera_valor_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
