CREATE OR REPLACE PROCEDURE pls_obter_se_dias_regra_prest ( nr_seq_requisicao_p bigint, nr_seq_guia_plano_p bigint, nr_seq_execucao_p bigint, nr_sequencia_p bigint, ie_tipo_item_p bigint, cd_espec_regra_p bigint, qt_dias_regra_p bigint, ie_valida_execucao_p text, ds_parametro2_p text, ds_parametro3_p text, nm_usuario_p text, ie_regra_dias_p INOUT text) AS $body$
DECLARE


dt_solicitacao_w		timestamp;
ie_regra_dias_w			varchar(1) := 'N';
qt_reg_w			bigint := 0;
nr_seq_segurado_w		bigint;
cd_espec_requisicao_w		integer;


BEGIN

if (ie_tipo_item_p in (5,6,9)) and (coalesce(ie_valida_execucao_p,'N') = 'N') then
	begin
		select	dt_requisicao,
			nr_seq_segurado,
			cd_especialidade
		into STRICT	dt_solicitacao_w,
			nr_seq_segurado_w,
			cd_espec_requisicao_w
		from	pls_requisicao
		where	nr_sequencia = nr_seq_requisicao_p;

		if (cd_espec_requisicao_w = cd_espec_regra_p) then
			select	count(1)
			into STRICT	qt_reg_w
			from	pls_requisicao
			where	nr_sequencia		<> nr_seq_requisicao_p
			and	nr_seq_segurado		= nr_seq_segurado_w
			and	cd_especialidade	= cd_espec_regra_p
			and	ie_estagio		= 2
			and	dt_solicitacao_w between trunc(dt_requisicao) and fim_dia(dt_requisicao + qt_dias_regra_p);
		end if;
	exception
	when others then
		qt_reg_w := 0;
	end;
elsif (ie_tipo_item_p in (10,11)) or (coalesce(ie_valida_execucao_p,'N') = 'S')then
	select	nr_seq_segurado,
		cd_especialidade
	into STRICT	nr_seq_segurado_w,
		cd_espec_requisicao_w
	from	pls_requisicao
	where	nr_sequencia = nr_seq_requisicao_p;

	dt_solicitacao_w := clock_timestamp();

	if (cd_espec_requisicao_w = cd_espec_regra_p) then
		select	count(1)
		into STRICT	qt_reg_w
		from	pls_requisicao	c,
			pls_execucao_requisicao b,
			pls_execucao_req_item a,
			pls_guia_plano d
		where	a.nr_seq_execucao	= b.nr_sequencia
		and	a.nr_seq_requisicao	= c.nr_sequencia
		and	a.nr_seq_guia		= d.nr_sequencia
		and	c.nr_seq_segurado	= nr_seq_segurado_w
		and	((b.nr_sequencia	<> nr_seq_execucao_p)
		or	((coalesce(nr_seq_execucao_p::text, '') = '' and ie_valida_execucao_p = 'S')
		and (c.nr_sequencia		<> nr_seq_requisicao_p)))
		and	c.cd_especialidade	= cd_espec_regra_p
		and	a.ie_situacao		in ('S','P')
		and	c.ie_estagio		not in (3,7)
		and 	d.ie_status		not in (3)
		and	dt_solicitacao_w 	between trunc(b.dt_execucao) and fim_dia(b.dt_execucao + qt_dias_regra_p);
	end if;

end if;

if (qt_reg_w > 0) then
	ie_regra_dias_w	:= 'S';
end if;

ie_regra_dias_p := ie_regra_dias_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
