CREATE OR REPLACE PROCEDURE pls_obter_se_item_utilizado ( nr_seq_guia_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, nr_seq_material_p bigint, cd_estabelecimento_p bigint, ie_opcao_p text, qt_utilizada_p INOUT bigint, qt_autorizada_p INOUT bigint, ie_autorizado_p INOUT text) AS $body$
DECLARE


/* ie_opcao_p
	'P' - Procedimento
	'M' - Material
	'I' - Dias internação
*/
qt_utilizada_w			double precision	:= 0;
qt_autorizada_w			double precision	:= 0;
nr_seq_guia_mat_w		bigint;
ie_autorizado_w			varchar(1)	:= 'S';


BEGIN

if (ie_opcao_p = 'P') and (cd_procedimento_p IS NOT NULL AND cd_procedimento_p::text <> '') then
	/* Buscar se existe guia não negada para o procedimento*/

	select	coalesce(max(qt_autorizada),0),
		coalesce(max(qt_utilizado),0)
	into STRICT	qt_autorizada_w,
		qt_utilizada_w
	from (
		SELECT	coalesce(sum(a.qt_autorizada),0) qt_autorizada,
			coalesce(sum(a.qt_utilizado),0) qt_utilizado
		from	pls_guia_plano_proc	a,
			pls_guia_plano    	b
		where	b.nr_sequencia = nr_seq_guia_p
		and	b.ie_status = '1'
		and	a.nr_seq_guia  = b.nr_sequencia
		and	a.ie_status in ('L','S','P')
		and	a.ie_origem_proced  = ie_origem_proced_p
		and	a.cd_procedimento  = cd_procedimento_p
		
union

		SELECT	coalesce(sum(a.qt_autorizada),0) qt_autorizada,
			coalesce(sum(a.qt_utilizado),0) qt_utilizado
		from	pls_guia_plano_proc  a,
			pls_guia_plano    b
		where	b.nr_seq_guia_principal = nr_seq_guia_p
		and	b.ie_status = '1'
		and	a.nr_seq_guia  = b.nr_sequencia
		and	a.ie_status in ('L','S','P')
		and	a.ie_origem_proced  = ie_origem_proced_p
		and	a.cd_procedimento  = cd_procedimento_p
	) alias16;

	if (not qt_autorizada_w > 0) then
		ie_autorizado_w := 'N';
	end if;

elsif (ie_opcao_p = 'M') and (nr_seq_material_p IS NOT NULL AND nr_seq_material_p::text <> '') then

	select	coalesce(max(qt_autorizada),0),
		coalesce(max(qt_utilizado),0)
	into STRICT	qt_autorizada_w,
		qt_utilizada_w
	from (
		SELECT	coalesce(sum(a.qt_autorizada),0) qt_autorizada,
			coalesce(sum(a.qt_utilizado),0) qt_utilizado
		from	pls_guia_plano_mat	a,
			pls_guia_plano		b
		where	b.nr_sequencia = nr_seq_guia_p
		and	b.ie_status = '1'
		and	a.nr_seq_guia  = b.nr_sequencia
		and	a.ie_status in ('L','S','P')
		and	a.nr_seq_material  = nr_seq_material_p
		
union

		SELECT	coalesce(sum(a.qt_autorizada),0) qt_autorizada,
			coalesce(sum(a.qt_utilizado),0) qt_utilizado
		from	pls_guia_plano_mat	a,
			pls_guia_plano		b
		where	b.nr_seq_guia_principal = nr_seq_guia_p
		and	b.ie_status = '1'
		and	a.nr_seq_guia  = b.nr_sequencia
		and	a.ie_status in ('L','S','P')
		and	a.nr_seq_material  = nr_seq_material_p
	) alias16;

	if (not qt_autorizada_w > 0) then
		ie_autorizado_w := 'N';
	end if;
end if;

qt_autorizada_p		:= coalesce(qt_autorizada_w,0);
qt_utilizada_p		:= coalesce(qt_utilizada_w,0);
ie_autorizado_p		:= coalesce(ie_autorizado_w,'S');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_obter_se_item_utilizado ( nr_seq_guia_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, nr_seq_material_p bigint, cd_estabelecimento_p bigint, ie_opcao_p text, qt_utilizada_p INOUT bigint, qt_autorizada_p INOUT bigint, ie_autorizado_p INOUT text) FROM PUBLIC;

