CREATE OR REPLACE PROCEDURE pls_obter_se_prest_envio ( nr_seq_prestador_p bigint, dt_referencia_p timestamp, ie_envio_p INOUT text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Obter se o prestador médico se encaixa em uma das regras de envio.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[ X ]  Objetos do dicionário [  ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
-------------------------------------------------------------------------------------------------------------------
Referências:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ie_envio_prest_w		varchar(255);
ie_envio_med_w			varchar(255);
ie_envio_w			varchar(255)	:= null;
nr_seq_regra_cpm_w		bigint;
nr_seq_tipo_prestador_w		bigint;
nr_seq_conselho_w		bigint;

C01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	pls_regra_envio_cpm	a
	where	dt_referencia_p between a.dt_inicio_vigencia and a.dt_fim_vigencia;


BEGIN
ie_envio_p	:= 'S';

select	max(a.nr_seq_tipo_prestador),
	max(b.nr_seq_conselho)
into STRICT	nr_seq_tipo_prestador_w,
	nr_seq_conselho_w
from	pessoa_fisica	b,
	pls_prestador	a
where	b.cd_pessoa_fisica	= a.cd_pessoa_fisica
and	a.nr_sequencia		= nr_seq_prestador_p;

open C01;
loop
fetch C01 into
	nr_seq_regra_cpm_w;
EXIT WHEN NOT FOUND or (ie_envio_w = 'S');  /* apply on C01 */
	begin
	ie_envio_prest_w := pls_obter_se_regra_prest(nr_seq_tipo_prestador_w, nr_seq_regra_cpm_w, nr_seq_prestador_p, ie_envio_prest_w);

	ie_envio_med_w := pls_obter_se_regra_med(nr_seq_conselho_w, nr_seq_regra_cpm_w, nr_seq_prestador_p, ie_envio_med_w);

	if (ie_envio_prest_w = 'S') and (ie_envio_med_w = 'S') then
		ie_envio_w	:= 'S';
	else
		ie_envio_w	:= 'N';
	end if;
	end;
end loop;
close C01;

if (C01%ISOPEN) then
	close C01;
end if;

ie_envio_p	:= coalesce(ie_envio_w, ie_envio_p);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
