CREATE OR REPLACE PROCEDURE pls_obter_se_protocolo_inser ( nr_seq_protocolo_p bigint, nm_usuario_p text, ds_retorno_p INOUT text) AS $body$
DECLARE


ie_situacao_w			varchar(3);
ds_retorno_w			varchar(1)	:= 'S';
ie_origem_protocolo_w		varchar(1);
ie_status_w			varchar(1);
qt_max_contas_protocolo_w	bigint;
qt_contas_protocolo_w		bigint;
ie_apresentacao_w		varchar(1) := 'A';


BEGIN
if (nr_seq_protocolo_p IS NOT NULL AND nr_seq_protocolo_p::text <> '') then
	select	qt_contas_informadas,
		ie_situacao,
		ie_origem_protocolo,
		ie_status,
		ie_apresentacao
	into STRICT	qt_max_contas_protocolo_w,
		ie_situacao_w,
		ie_origem_protocolo_w,
		ie_status_w,
		ie_apresentacao_w
	from	pls_protocolo_conta
	where	nr_sequencia	= nr_seq_protocolo_p;

	if (coalesce(ie_situacao_w,'D') <> 'D') and (coalesce(ie_apresentacao_w,'A') = 'A')then
		ds_retorno_w := 'N';
	end if;

	if (ie_status_w not in ('1','2'))	then
		ds_retorno_w := 'N';
	end if;

	if (ie_origem_protocolo_w <> 'D') then
		if 	(((obter_valor_param_usuario(1208,52,obter_perfil_ativo,nm_usuario_p,0) = 'S') and (ie_origem_protocolo_w = 'P')) or (coalesce(ie_apresentacao_w,'A') = 'R'))then
			ds_retorno_w	:= ds_retorno_w;
		else
			ds_retorno_w	:= 'N';
		end if;
	end if;

	select	count(1)
	into STRICT	qt_contas_protocolo_w
	from	pls_conta
	where	nr_seq_protocolo = nr_seq_protocolo_p;

	/*if	(qt_contas_protocolo_w >= qt_max_contas_protocolo_w ) and
		(qt_max_contas_protocolo_w is not null) then
		ds_retorno_w := 'N';
	end if;*/
end if;

ds_retorno_p := ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
