CREATE OR REPLACE PROCEDURE pls_obter_se_regra_prest ( nr_seq_tipo_prestador_p bigint, nr_seq_regra_p bigint, nr_seq_prestador_p bigint, ie_envio_p INOUT text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Obter se o prestador médico se encaixa em uma das regras de envio.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[ X ]  Objetos do dicionário [  ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
-------------------------------------------------------------------------------------------------------------------
Referências:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ie_envio_w			varchar(255)	:= null;
nr_seq_tipo_prestador_w		bigint;

C01 CURSOR FOR
	SELECT	a.nr_seq_tipo_prestador
	from	pls_regra_envio_cpm_prest	a
	where	a.nr_seq_regra	= nr_seq_regra_p
	order by
		coalesce(a.nr_seq_tipo_prestador, 0) desc;


BEGIN
ie_envio_p	:= 'S';

open C01;
loop
fetch C01 into
	nr_seq_tipo_prestador_w;
EXIT WHEN NOT FOUND or (ie_envio_w = 'S');  /* apply on C01 */
	begin
	if (coalesce(nr_seq_tipo_prestador_p::text, '') = '') or (coalesce(nr_seq_tipo_prestador_w::text, '') = '') or (nr_seq_tipo_prestador_w = nr_seq_tipo_prestador_p) then
		ie_envio_w	:= 'S';
	else
		ie_envio_w	:= 'N';
	end if;
	end;
end loop;
close C01;

ie_envio_p	:= coalesce(ie_envio_w, ie_envio_p);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
