SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_obter_se_restr_mat_duplic ( nr_seq_material_p bigint, nr_sequencia_p bigint, dt_inicial_p timestamp, dt_final_p timestamp) AS $body$
DECLARE


ie_retorno_w		varchar(1);
qt_reg_w		integer;
nr_seq_regra_w		bigint	:= null;
nr_seq_material_w	bigint;



BEGIN
/* Verificar primeiro se já tem um registro com datas nulas */

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	nr_seq_material_w	:= nr_seq_material_p;

	if (nr_seq_material_w <> 0) then

		select	count(*)
		into STRICT	qt_reg_w
		from	pls_material_restricao a
		where	coalesce(a.dt_inicio_vigencia::text, '') = ''
		and	a.nr_seq_material	= nr_seq_material_w
		and	a.nr_sequencia <> nr_sequencia_p;

		if (qt_reg_w > 1) then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(265238,'');
			--Mensagem: Só é permitida uma restrição sem data de início de vigência para este material.
		end if;

		select	count(*)
		into STRICT	qt_reg_w
		from	pls_material_restricao a
		where	coalesce(a.dt_fim_vigencia::text, '') = ''
		and	a.nr_seq_material	= nr_seq_material_w
		and	a.nr_sequencia <> nr_sequencia_p;

		if (qt_reg_w > 1) then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(265237,'');
			--Mensagem: Só é permitida uma restrição sem data de fim de vigência para este material.
		end if;

		/* Verificar se a data inicial nova esta entre as duas datas */

		select	max(a.nr_sequencia)
		into STRICT	nr_seq_regra_w
		from	pls_material_restricao a
		where	a.nr_seq_material	= nr_seq_material_w
		and	dt_inicial_p between coalesce(a.dt_inicio_vigencia,dt_inicial_p) and coalesce(a.dt_fim_vigencia,dt_inicial_p)
		and	a.nr_sequencia <> nr_sequencia_p;
		/* Verificart se a data final nova esta entre as duas datas */

		if (coalesce(nr_seq_regra_w::text, '') = '') then
			select	max(a.nr_sequencia)
			into STRICT	qt_reg_w
			from	pls_material_restricao a
			where	a.nr_seq_material	= nr_seq_material_w
			and	dt_final_p between coalesce(a.dt_inicio_vigencia,dt_final_p) and coalesce(a.dt_fim_vigencia,dt_final_p)
			and	a.nr_sequencia <> nr_sequencia_p;
		end if;
		/* Verificar se a data inicial da regra esta entre a data inicial e final nova */

		if (coalesce(nr_seq_regra_w::text, '') = '') then
			select	max(a.nr_sequencia)
			into STRICT	nr_seq_regra_w
			from	pls_material_restricao a
			where	a.nr_seq_material	= nr_seq_material_w
			and	a.dt_inicio_vigencia between coalesce(dt_inicial_p,a.dt_inicio_vigencia) and coalesce(dt_final_p,a.dt_inicio_vigencia)
			and	a.nr_sequencia <> nr_sequencia_p;
		end if;
		/* Verificar se data final da regra esta entre a data inicial e final nova */

		if (coalesce(nr_seq_regra_w::text, '') = '') then
			select	max(a.nr_sequencia)
			into STRICT	nr_seq_regra_w
			from	pls_material_restricao a
			where	a.nr_seq_material	= nr_seq_material_w
			and	a.dt_fim_vigencia between coalesce(dt_inicial_p,a.dt_fim_vigencia) and coalesce(dt_final_p,a.dt_fim_vigencia)
			and	a.nr_sequencia <> nr_sequencia_p;
		end if;

		if (nr_seq_regra_w IS NOT NULL AND nr_seq_regra_w::text <> '') then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(265232,'SEQ_REGRA='||nr_seq_regra_w);
			--Mensagem: A regra #@ seq_regra#@ está em conflito de vigência com a regra informada!
		end if;
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
