CREATE OR REPLACE PROCEDURE pls_obter_se_sca_acomodacao ( nr_seq_segurado_p bigint, nr_seq_tipo_acomodacao_p bigint, dt_soliciticao_p timestamp, ie_tipo_guia_p text, ie_coberto_p INOUT text) AS $body$
DECLARE


ie_coberto_w			varchar(10) := 'N';
qt_registros_w			bigint;
nr_seq_categoria_w		bigint;

C01 CURSOR(	nr_seq_segurado_pc	bigint,
		dt_soliciticao_pc	timestamp) FOR
	SELECT	nr_seq_plano
	from	pls_sca_vinculo
	where	nr_seq_segurado	= nr_seq_segurado_pc
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
	and	((coalesce(dt_fim_vigencia::text, '') = '') or (dt_fim_vigencia > dt_soliciticao_pc));

BEGIN

if (nr_seq_tipo_acomodacao_p IS NOT NULL AND nr_seq_tipo_acomodacao_p::text <> '') then
	for r_c01_w in C01(nr_seq_segurado_p,dt_soliciticao_p) loop
		begin

		select	count(1)
		into STRICT	qt_registros_w
		from	pls_plano_acomodacao
		where	nr_seq_plano		= r_c01_w.nr_seq_plano
		and	nr_seq_tipo_acomodacao	= nr_seq_tipo_acomodacao_p;

		if (qt_registros_w	> 0) then
			ie_coberto_w	:= 'S';
			exit;
		elsif (ie_tipo_guia_p = '1') then
			select	coalesce(max(a.nr_seq_categoria),0)
			into STRICT	nr_seq_categoria_w
			from	pls_plano_acomodacao a
			where	a.nr_seq_plano	= r_c01_w.nr_seq_plano
			and	exists (	SELECT	1
						from	pls_regra_categoria b
						where	b.nr_seq_categoria		= a.nr_seq_categoria
						and	b.nr_seq_tipo_acomodacao	= nr_seq_tipo_acomodacao_p);

			if (nr_seq_categoria_w <> 0) then
				ie_coberto_w	:= 'S';
				exit;
			end if;
		end if;
		end;
	end loop;
end if;

ie_coberto_p	:= ie_coberto_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_obter_se_sca_acomodacao ( nr_seq_segurado_p bigint, nr_seq_tipo_acomodacao_p bigint, dt_soliciticao_p timestamp, ie_tipo_guia_p text, ie_coberto_p INOUT text) FROM PUBLIC;

