CREATE OR REPLACE PROCEDURE pls_obter_tipo_vl_copartic_aut ( nr_seq_segurado_p bigint, dt_guia_p timestamp, nm_usuario_p text, ie_tipo_despesa_p text, ie_tipo_valor_p INOUT text, nr_seq_regra_P INOUT bigint) AS $body$
DECLARE


nr_seq_regra_w				bigint;
nr_seq_contrato_w			bigint;
ie_tipo_valor_w				varchar(2);

C01 CURSOR FOR
	SELECT	nr_sequencia,
			ie_tipo_valor
	from	pls_regra_copartic_aut
	where	ie_situacao		= 'A'
	and (coalesce(nr_seq_contrato::text, '') = ''	or nr_seq_contrato	= nr_seq_contrato_w)
	and (coalesce(ie_tipo_despesa::text, '') = ''	or ie_tipo_despesa	= ie_tipo_despesa_p)
	and	dt_guia_p between dt_inicio_vigencia and fim_dia(coalesce(dt_fim_vigencia,dt_guia_p))
	and (coalesce(cd_estabelecimento, wheb_usuario_pck.get_cd_estabelecimento) = wheb_usuario_pck.get_cd_estabelecimento and pls_obter_se_controle_estab('RE') = 'S')
	
union all

	SELECT	nr_sequencia,
			ie_tipo_valor
	from	pls_regra_copartic_aut
	where	ie_situacao		= 'A'
	and (coalesce(nr_seq_contrato::text, '') = ''	or nr_seq_contrato	= nr_seq_contrato_w)
	and (coalesce(ie_tipo_despesa::text, '') = ''	or ie_tipo_despesa	= ie_tipo_despesa_p)
	and	dt_guia_p between dt_inicio_vigencia and fim_dia(coalesce(dt_fim_vigencia,dt_guia_p))
	and 	pls_obter_se_controle_estab('RE') = 'N';


BEGIN

begin
select 	nr_seq_contrato
into STRICT	nr_seq_contrato_w
from 	pls_segurado
where	nr_sequencia	= nr_seq_segurado_p;
exception
	when others then
	nr_seq_contrato_w	:= null;
end;

open C01;
loop
fetch C01 into
	nr_seq_regra_w,
	ie_tipo_valor_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
end loop;
close C01;

ie_tipo_valor_p		:= ie_tipo_valor_w;
nr_seq_regra_p 		:= nr_seq_regra_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
