CREATE OR REPLACE PROCEDURE pls_obter_todas_macros ( ds_separador_p text, cd_pessoa_fisica_p text, nr_sequencia_p bigint, ie_opcao_p text, ds_macros_p INOUT text) AS $body$
DECLARE

 
ds_separador_w	varchar(1);
ds_macro_w	varchar(20);
nm_atributo_w	varchar(50);
ds_resultado_w	varchar(10);
ds_substituir_w	varchar(255);

C01 CURSOR FOR 
	SELECT	upper(ds_macro), 
		upper(nm_atributo) 
	from	pls_macro_plano 
	order by	ds_macro;


BEGIN 
ds_macros_p	:= '';
ds_separador_w	:= ds_separador_p;
if (coalesce(ds_separador_w::text, '') = '') then 
	ds_separador_w	:= '#';
end if;
 
open C01;
loop 
fetch C01 into	 
	ds_macro_w, 
	nm_atributo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	if (nm_atributo_w = 'CD_PESSOA_FISICA') then 
		ds_resultado_w	:= cd_pessoa_fisica_p;
	elsif (nm_atributo_w = 'NR_SEQ_CONTRATO') then 
		ds_resultado_w	:= nr_sequencia_p;
	end if;
 
	select	substr(pls_substituir_macro(ds_macro_w, nm_atributo_w, ds_resultado_w, ie_opcao_p), 1,255) 
	into STRICT	ds_substituir_w 
	;
 
	ds_macros_p	:= ds_macros_p || ds_separador_p || ds_macro_w || ds_separador_p || coalesce(ds_substituir_w, ' ');
	end;
end loop;
close C01;
 
ds_macros_p	:= substr(ds_macros_p, 2, length(ds_macros_p));
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
