CREATE OR REPLACE PROCEDURE pls_obter_valores_agravo ( nr_seq_agravo_p bigint, ie_tipo_agravo_p text, qt_parcelas_p INOUT bigint, vl_agravo_total_p INOUT bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_parcelas_w			bigint;
vl_agravo_total_w		double precision;
qt_regras_w			bigint;


BEGIN

select	count(*)
into STRICT	qt_regras_w
from	pls_agravo_lote_valor	a,
	pls_agravo_valor	b
where	b.nr_seq_lote		= a.nr_sequencia
and	a.nr_seq_agravo		= nr_seq_agravo_p
and	((a.ie_classificacao	= ie_tipo_agravo_p) or (coalesce(a.ie_classificacao::text, '') = ''))
and	clock_timestamp() between coalesce(a.dt_inicio_vigencia,clock_timestamp()) and fim_dia(coalesce(a.dt_fim_vigencia,clock_timestamp()));

if (qt_regras_w = 1) then
	select	max(b.qt_parcelas),
		max(b.vl_agravo)
	into STRICT	qt_parcelas_w,
		vl_agravo_total_w
	from	pls_agravo_lote_valor	a,
		pls_agravo_valor	b
	where	b.nr_seq_lote		= a.nr_sequencia
	and	a.nr_seq_agravo		= nr_seq_agravo_p
	and	((a.ie_classificacao	= ie_tipo_agravo_p) or (coalesce(a.ie_classificacao::text, '') = ''))
	and	clock_timestamp() between coalesce(a.dt_inicio_vigencia,clock_timestamp()) and fim_dia(coalesce(a.dt_fim_vigencia,clock_timestamp()));
end if;

qt_parcelas_p		:= qt_parcelas_w;
vl_agravo_total_p	:= vl_agravo_total_w;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
