CREATE OR REPLACE PROCEDURE pls_obter_valores_lib_analise (nr_seq_item_resumo_p bigint, cd_estabelecimento_p bigint, vl_glosa_p INOUT bigint, vl_liberado_p INOUT bigint, qt_liberada_p INOUT bigint, vl_liberado_co_p INOUT bigint, vl_liberado_material_p INOUT bigint, vl_liberado_hi_p INOUT bigint, vl_glosa_co_p INOUT bigint, vl_glosa_material_p INOUT bigint, vl_glosa_hi_p INOUT bigint) AS $body$
DECLARE


vl_base_glosa_w			double precision := 0;
vl_base_w			double precision;
vl_liberado_co_w		double precision;
vl_liberado_material_w		double precision;
vl_liberado_hi_w		double precision;
vl_liberado_w			double precision;
qt_liberada_w			double precision;
vl_glosa_co_w			double precision;
vl_glosa_material_w		double precision;
vl_glosa_hi_w			double precision;
ie_valor_base_w			double precision;
vl_calculado_w			double precision;
vl_apresentado_w		double precision;
qt_apresentado_w		double precision;
ie_calculo_base_glosa_w		varchar(1)	:= 'N';
vl_calculado_mat_w		double precision;
vl_calculado_co_w		double precision;
vl_calculado_hi_w		double precision;
vl_glosa_w			double precision;


BEGIN
if (nr_seq_item_resumo_p IS NOT NULL AND nr_seq_item_resumo_p::text <> '') then
	select	coalesce(max(ie_calculo_base_glosa),'N')
	into STRICT	ie_calculo_base_glosa_w
	from	pls_parametros
	where	cd_estabelecimento = cd_estabelecimento_p;

	select	a.ie_valor_base,
		coalesce(a.vl_total_apres,0),
		coalesce(a.vl_calculado,0),
		coalesce(a.qt_apresentado,0),
		coalesce(a.vl_calculado_co,0),
		coalesce(a.vl_calculado_material,0),
		coalesce(a.vl_calculado_hi,0)
	into STRICT	ie_valor_base_w,
		vl_apresentado_w,
		vl_calculado_w,
		qt_apresentado_w,
		vl_calculado_co_w,
		vl_calculado_mat_w,
		vl_calculado_hi_w
	from	w_pls_resumo_conta a
	where	a.nr_sequencia	= nr_seq_item_resumo_p;

	if (ie_valor_base_w = 1) then
		vl_base_w	:= vl_apresentado_w;
	else
		vl_base_w	:= vl_calculado_w;
	end if;

	if (vl_apresentado_w > 0) then
		vl_base_glosa_w	:= vl_apresentado_w;
	end if;

	vl_liberado_w		:= coalesce(vl_liberado_p,vl_base_w);
	vl_glosa_w		:= vl_base_glosa_w - vl_liberado_w;
	qt_liberada_w		:= coalesce(qt_liberada_p,qt_apresentado_w);

	vl_liberado_co_w	:= coalesce(vl_liberado_co_p,pls_obter_valor_proporcional(vl_calculado_w,vl_calculado_co_w,vl_liberado_w));
	vl_liberado_material_w	:= coalesce(vl_liberado_material_p,pls_obter_valor_proporcional(vl_calculado_w,vl_calculado_mat_w,vl_liberado_w));
	vl_liberado_hi_w	:= coalesce(vl_liberado_hi_p,pls_obter_valor_proporcional(vl_calculado_w,vl_calculado_hi_w,vl_liberado_w));

	/*if	(nvl(vl_liberado_co_p,0) = 0) and
		(nvl(vl_liberado_material_p,0) = 0) and
		(nvl(vl_liberado_hi_p,0) = 0) then
		vl_glosa_co_w	:= pls_obter_valor_proporcional(vl_calculado_w,vl_calculado_co_w,vl_glosa_w);
		vl_glosa_material_w	:= pls_obter_valor_proporcional(vl_calculado_w,vl_calculado_mat_w,vl_glosa_w);
		vl_glosa_hi_w	:= pls_obter_valor_proporcional(vl_calculado_w,vl_calculado_hi_w,vl_glosa_w);
	else
		vl_glosa_co_w	:= vl_calculado_co_w - vl_liberado_co_w;
		vl_glosa_material_w := vl_calculado_mat_w - vl_liberado_material_w;
		vl_glosa_hi_w	:= vl_calculado_hi_w - vl_liberado_hi_w;

		vl_glosa_w	:= vl_glosa_co_w + vl_glosa_material_w + vl_glosa_hi_w;
	end if;*/
	if (vl_glosa_w < 0) then
		vl_glosa_w := 0;
	end if;

	vl_glosa_p	:= vl_glosa_w;
	vl_liberado_p	:= vl_liberado_w;
	qt_liberada_p	:= qt_liberada_w;
	vl_liberado_co_p	:= vl_liberado_co_w;
	vl_liberado_material_p	:= vl_liberado_material_w;
	vl_liberado_hi_p	:= vl_liberado_hi_w;
	vl_glosa_co_p		:= vl_glosa_co_w;
	vl_glosa_material_p	:= vl_glosa_material_w;
	vl_glosa_hi_p		:= vl_glosa_hi_w;
end if;
/* NÃ£o pode commit aqui */

end;
$body$
