CREATE OR REPLACE PROCEDURE pls_obter_valores_qr_code ( ds_qr_code_p text, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, cd_usuario_plano_p out text, nm_beneficiario_p out text, dt_nascimento_p out text, ie_sexo_p out text, dt_validade_p out text, cd_plano_qrcode_p out text, ds_cns_p out text, ds_rede_p out text, nr_via_solicitacao_p out text, ie_cartao_p out text, nm_social_p out text, ds_token_p out text, ie_versao_p out text, cd_operadora_p out text) is ds_qr_code_w varchar(4000) AS $body$
DECLARE

ds_retorno_w	varchar(255);
BEGIN
ds_retorno_w	:= substr(dt_base_p, 1, 2) || '/' || substr(dt_base_p, 4, 2) || '/' || substr(dt_base_p, 7, 4);

return;

end;

begin

if (ds_qr_code_p IS NOT NULL AND ds_qr_code_p::text <> '') then
	if (substr(ds_qr_code_p,1,1) not in ('1','2','3')) then
		ds_qr_code_w	:= substr(ds_qr_code_p,2,length(ds_qr_code_p));
	else
		ds_qr_code_w	:= ds_qr_code_p;
	end if;
	
	for i in 1..13 loop
		begin
		if (ds_qr_code_w like '%=%') then
			select	substr(ds_qr_code_w, 1,length(ds_qr_code_w) - length(substr(ds_qr_code_w, position('=' in ds_qr_code_w), 999)))
			into STRICT 	ds_retorno_w
			;
		else
			ds_retorno_w	:= ds_qr_code_w;
		end if;
		
		if (i = 1) then
			ie_cartao_p	:= ds_retorno_w;
		end if;
		
		if (ie_cartao_p = 1) then --Cartão Físico
			if (i = 2) then
				ie_versao_p		:= ds_retorno_w;
			elsif (i = 3) then
				cd_usuario_plano_p 	:= ds_retorno_w;
				cd_operadora_p		:= substr(ds_retorno_w, 2, 3);
			elsif (i = 4) then
				nm_beneficiario_p	:= ds_retorno_w;
			elsif (i = 5) then
				dt_nascimento_p		:= formatar_data(ds_retorno_w);
			elsif (i = 6) then
				ie_sexo_p		:= ds_retorno_w;
			elsif (i = 7) then
				ds_cns_p		:= ds_retorno_w;
			elsif (i = 8) then
				dt_validade_p		:= formatar_data(ds_retorno_w);
			elsif (i = 9) then
				nr_via_solicitacao_p    := ds_retorno_w;
			elsif (i = 10) then
				ds_rede_p		:= ds_retorno_w;
			elsif (i = 11) then
				cd_plano_qrcode_p	:= ds_retorno_w;
			elsif (i = 12) then
				nm_social_p		:= ds_retorno_w;
			end if;
		elsif (ie_cartao_p in (2,3))  then -- Cartão Virtual
			if (i = 2) then
				ie_versao_p		:= ds_retorno_w;
			elsif (i = 3) then
				cd_usuario_plano_p 	:= ds_retorno_w;
				cd_operadora_p		:= substr(ds_retorno_w, 2, 3);
			elsif (i = 4) then
				nm_beneficiario_p	:= ds_retorno_w;
			elsif (i = 5) then
				dt_nascimento_p		:= formatar_data(ds_retorno_w);
			elsif (i = 6) then
				ie_sexo_p		:= ds_retorno_w;
			elsif (i = 7) then
				ds_cns_p		:= ds_retorno_w;
			elsif (i = 8) then
				dt_validade_p		:= formatar_data(ds_retorno_w);
			elsif (i = 9) then
				ds_rede_p		:= ds_retorno_w;
			elsif (i = 10) then
				cd_plano_qrcode_p	:= ds_retorno_w;
			elsif (i = 11) then
				nm_social_p		:= ds_retorno_w;
			elsif (i = 12) then
				ds_token_p		:= ds_retorno_w;
			end if;
		end if;
		
		ds_qr_code_w := substr(ds_qr_code_w, position('=' in ds_qr_code_w)+1, 999);
		end;
	end loop;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
