CREATE OR REPLACE PROCEDURE pls_obter_valores_reajuste ( nr_contrato_p bigint, dt_inicial_p timestamp, dt_final_p timestamp, cd_motivo_reajuste_p text, ie_tipo_contrato_p text, ie_plano_sca_p text, ie_situacao_beneficiario_p text, vl_preco_ant_p INOUT bigint, vl_preco_atual_p INOUT bigint, vl_reajuste_p INOUT bigint) AS $body$
DECLARE


vl_preco_ant_w		double precision;
vl_preco_atual_w	double precision;
vl_reajuste_w		double precision;


BEGIN

select	sum(vl_preco_ant),
	sum(vl_preco_atual),
	sum(vl_reajuste)
into STRICT	vl_preco_ant_w,
	vl_preco_atual_w,
	vl_reajuste_w
from	pls_segurado_preco_v
where	ie_tipo_contrato	= ie_tipo_contrato_p
and	cd_motivo_reajuste in ('I','E')
and	((nr_contrato = nr_contrato_p) or (coalesce(nr_contrato_p,0) = 0))
and	((cd_motivo_reajuste = cd_motivo_reajuste_p) or (coalesce(cd_motivo_reajuste_p,'0') = '0'))
and	((ie_plano_sca = ie_plano_sca_p) or (coalesce(ie_plano_sca_p,'A') = 'A'))
and	dt_reajuste >= trunc(dt_inicial_p,'dd')
and	dt_reajuste <= fim_dia(dt_final_p)
and	((ie_situacao_beneficiario_p='1' and (coalesce(dt_rescisao::text, '') = '' or dt_rescisao >= clock_timestamp()))
     or (ie_situacao_beneficiario_p='2' and ((dt_rescisao IS NOT NULL AND dt_rescisao::text <> '') or dt_rescisao < clock_timestamp()))
     or (coalesce(ie_situacao_beneficiario_p,'0')='0'));


vl_preco_ant_p	:= vl_preco_ant_w;
vl_preco_atual_p	:= vl_preco_atual_w;
vl_reajuste_p	:= vl_reajuste_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
