CREATE OR REPLACE PROCEDURE pls_obter_valor_composicao ( nr_seq_composicao_P bigint, ie_proc_mat_p text, cd_estabelecimento_p bigint, nm_usuario_p text, vl_composicao_p INOUT bigint) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
Obter o valor de composição restringindo a princípio por procedimento 'P', ou material 'M' podendo no futuro trazer o valor total da mesma
OS 558331,
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
vl_composicao_w		double precision;

BEGIN

if (ie_proc_mat_p = 'P') then
	select	coalesce(sum(vl_negociado),0)
	into STRICT	vl_composicao_w
	from	pls_pacote_procedimento
	where	nr_seq_composicao = nr_seq_composicao_p
	and	ie_situacao = 'A';
elsif (ie_proc_mat_p = 'M') then
	select	coalesce(sum(vl_negociado),0)
	into STRICT	vl_composicao_w
	from	pls_pacote_material
	where	nr_seq_composicao = nr_seq_composicao_p
	and	ie_situacao = 'A';
end if;

vl_composicao_p := vl_composicao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_obter_valor_composicao ( nr_seq_composicao_P bigint, ie_proc_mat_p text, cd_estabelecimento_p bigint, nm_usuario_p text, vl_composicao_p INOUT bigint) FROM PUBLIC;

