CREATE OR REPLACE PROCEDURE pls_obter_valor_pagamento ( pr_liberado_p bigint, vl_materiais_p bigint, vl_custo_operacional_p bigint, vl_taxa_adm_mat_p INOUT bigint, vl_taxa_adm_co_p INOUT bigint, pr_materiais_p INOUT bigint, pr_custo_operacional_p INOUT bigint, vl_liberado_p INOUT bigint, vl_taxa_adm_p INOUT bigint) AS $body$
DECLARE


vl_taxa_adm_w		double precision	:= 0;
vl_taxa_adm_co_w	double precision	:= 0;
vl_taxa_adm_mat_w	double precision	:= 0;

BEGIN
if (coalesce(pr_liberado_p,0) > 0) then

	vl_taxa_adm_w	:= dividir((vl_liberado_p * pr_liberado_p),100);
	vl_liberado_p	:= vl_liberado_p - vl_taxa_adm_w;
else
	if (coalesce(pr_custo_operacional_p,0) > 0) and (coalesce(vl_custo_operacional_p,0) > 0) and (coalesce(vl_liberado_p,0) > 0) then
		vl_taxa_adm_co_w	:= dividir((coalesce(vl_custo_operacional_p,0) * pr_custo_operacional_p),100);
		vl_liberado_p		:= vl_liberado_p - vl_taxa_adm_co_w;
	end if;

	if (coalesce(pr_materiais_p,0) > 0) and (coalesce(vl_materiais_p,0) > 0) and (coalesce(vl_liberado_p,0) > 0) then
		vl_taxa_adm_mat_w	:= dividir((vl_materiais_p * pr_materiais_p),100);
		vl_liberado_p		:= vl_liberado_p - vl_taxa_adm_mat_w;
	end if;
end if;

vl_taxa_adm_p		:= coalesce(vl_taxa_adm_w,vl_taxa_adm_p);
vl_taxa_adm_co_p	:= coalesce(vl_taxa_adm_co_w,vl_taxa_adm_co_p);
vl_taxa_adm_mat_p	:= coalesce(vl_taxa_adm_mat_w,vl_taxa_adm_mat_p);

end;
$body$
