SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_obter_venc_fatura (nr_seq_fatura_p bigint, cd_estabelecimento_p bigint, dt_vencimento_p INOUT timestamp) AS $body$
DECLARE


ds_vencimentos_w		varchar(4000);
nr_seq_congenere_w		bigint;
nr_seq_pagador_w		bigint;
nr_seq_camara_w			bigint;
cd_condicao_pagamento_w		bigint;
qt_vencimentos_w		bigint;
qt_dias_venc_w			bigint;
ie_forma_pagamento_w		integer;
dt_dia_vencimento_w		smallint;
dt_vencimento_w			timestamp;
dt_mesano_referencia_w		timestamp;

C01 CURSOR FOR
	SELECT	a.qt_dias_venc
	from	ptu_regra_venc_fatura	a
	where	coalesce(a.nr_seq_camara, coalesce(nr_seq_camara_w,0))		= coalesce(nr_seq_camara_w,0)
	and	coalesce(a.nr_seq_congenere, coalesce(nr_seq_congenere_w,0))	= coalesce(nr_seq_congenere_w,0)
	order by
		coalesce(a.nr_seq_camara, 0),
		coalesce(a.nr_seq_congenere, 0);


BEGIN
if (nr_seq_fatura_p IS NOT NULL AND nr_seq_fatura_p::text <> '') then
	select	a.nr_seq_congenere,
		a.nr_seq_pagador
	into STRICT	nr_seq_congenere_w,
		nr_seq_pagador_w
	from	pls_fatura a
	where	nr_sequencia = nr_seq_fatura_p;

	if (nr_seq_congenere_w IS NOT NULL AND nr_seq_congenere_w::text <> '') then
		select	max(a.nr_seq_camara)
		into STRICT	nr_seq_camara_w
		from	pls_congenere_camara a
		where	a.nr_seq_congenere = nr_seq_congenere_w
		and	clock_timestamp() between a.dt_inicio_vigencia_ref and a.dt_fim_vigencia_ref;

		open C01;
		loop
		fetch C01 into
			qt_dias_venc_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			begin
			null;
			end;
		end loop;
		close C01;

		select	clock_timestamp() + qt_dias_venc_w
		into STRICT	dt_vencimento_w
		;

		select	obter_proximo_dia_util(cd_estabelecimento_p, dt_vencimento_w)
		into STRICT	dt_vencimento_w
		;
	elsif (nr_seq_pagador_w IS NOT NULL AND nr_seq_pagador_w::text <> '') then
		select	max(dt_mesano_referencia)
		into STRICT	dt_mesano_referencia_w
		from	pls_fatura b,
			pls_lote_faturamento a
		where	a.nr_sequencia = b.nr_seq_lote
		and	b.nr_sequencia = nr_seq_fatura_p;

		select	max(a.dt_dia_vencimento),
			max(a.cd_condicao_pagamento)
		into STRICT	dt_dia_vencimento_w,
			cd_condicao_pagamento_w
		from	pls_contrato_pagador_fin a
		where	a.nr_seq_pagador	= nr_seq_pagador_w
		and	dt_mesano_referencia_w between trunc(a.dt_inicio_vigencia,'dd') and (trunc(coalesce(a.dt_fim_vigencia,dt_mesano_referencia_w),'dd') + 86399/86400);

		select 	coalesce(max(ie_forma_pagamento),3)
		into STRICT 	ie_forma_pagamento_w
		from 	condicao_pagamento
		where 	cd_condicao_pagamento	= cd_condicao_pagamento_w
		and 	ie_situacao           	= 'A';

		if (cd_condicao_pagamento_w IS NOT NULL AND cd_condicao_pagamento_w::text <> '') and (dt_dia_vencimento_w IS NOT NULL AND dt_dia_vencimento_w::text <> '') then
			SELECT * FROM Calcular_Vencimento(	cd_estabelecimento_p, cd_condicao_pagamento_w, clock_timestamp(), qt_vencimentos_w, ds_vencimentos_w) INTO STRICT qt_vencimentos_w, ds_vencimentos_w;

			if (qt_vencimentos_w = 1) then
				dt_vencimento_w		:= to_date(substr(ds_vencimentos_w,1,10),'dd/mm/yyyy');
			elsif (qt_vencimentos_w > 1) then
				null;
			end if;
		end if;
	end if;
	if (dt_vencimento_w IS NOT NULL AND dt_vencimento_w::text <> '') then
		dt_vencimento_p := dt_vencimento_w;
	end if;
end if;
end 	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_obter_venc_fatura (nr_seq_fatura_p bigint, cd_estabelecimento_p bigint, dt_vencimento_p INOUT timestamp) FROM PUBLIC;

