CREATE OR REPLACE PROCEDURE pls_obter_vendedor_pessoa ( cd_pessoa_fisica_p pessoa_fisica.cd_pessoa_fisica%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nr_seq_canal_venda_p INOUT pls_vendedor.nr_sequencia%type, nr_seq_vendedor_p INOUT pls_vendedor_vinculado.nr_sequencia%type, ie_permite_visualizar_canal_p INOUT pls_vendedor_vinculado.ie_permite_visualizar_canal%type) AS $body$
DECLARE


nr_seq_canal_venda_w		pls_vendedor.nr_sequencia%type				:= null;
nr_seq_vendedor_w		pls_vendedor_vinculado.nr_sequencia%type		:= null;
ie_permite_visualizar_canal_w	pls_vendedor_vinculado.ie_permite_visualizar_canal%type	:= 'N';


BEGIN
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	select	max(nr_sequencia)
	into STRICT	nr_seq_canal_venda_w
	from	pls_vendedor
	where	cd_pessoa_fisica = cd_pessoa_fisica_p
	and	coalesce(ie_situacao,'A')  = 'A'
	and	cd_estabelecimento = cd_estabelecimento_p;

	if (coalesce(nr_seq_canal_venda_w::text, '') = '') then
		select	max(a.nr_sequencia)
		into STRICT	nr_seq_canal_venda_w
		from	pls_vendedor		a,
			pls_vendedor_vinculado	b
		where	b.nr_seq_vendedor	= a.nr_sequencia
		and	b.cd_pessoa_fisica	= cd_pessoa_fisica_p
		and	a.cd_estabelecimento	= cd_estabelecimento_p
		and	coalesce(a.ie_situacao,'A')	= 'A'
		and	(a.cd_cgc IS NOT NULL AND a.cd_cgc::text <> '');

		if (nr_seq_canal_venda_w IS NOT NULL AND nr_seq_canal_venda_w::text <> '') then
			select	max(a.nr_sequencia)
			into STRICT	nr_seq_vendedor_w
			from	pls_vendedor_vinculado	a,
				pls_vendedor		b
			where	b.nr_sequencia	= a.nr_seq_vendedor
			and	(b.cd_cgc IS NOT NULL AND b.cd_cgc::text <> '')
			and	b.nr_sequencia	= nr_seq_canal_venda_w
			and	a.cd_pessoa_fisica = cd_pessoa_fisica_p
			and	coalesce(b.ie_situacao,'A')  = 'A';

			if (nr_seq_vendedor_w IS NOT NULL AND nr_seq_vendedor_w::text <> '') then
				select	coalesce(max(ie_permite_visualizar_canal),'N')
				into STRICT	ie_permite_visualizar_canal_w
				from	pls_vendedor_vinculado
				where	nr_sequencia	= nr_seq_vendedor_w;
			end if;
		end if;
	end if;
end if;

nr_seq_canal_venda_p		:= nr_seq_canal_venda_w;
nr_seq_vendedor_p		:= nr_seq_vendedor_w;
ie_permite_visualizar_canal_p	:= ie_permite_visualizar_canal_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
