SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_oc_cta_aplic_val_limi_proc ( dados_regra_p pls_tipos_ocor_pck.dados_regra, nr_id_transacao_p pls_oc_cta_selecao_ocor_v.nr_id_transacao%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	Aplicar a validação da limitação para os procedimentos cadastrados para as limitações dos
	benenficiários das contas filtradas para a ocorrência.
-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[X]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------

Pontos de atenção:

AO ALTERARA VERIFICAR A NECESSIDADE DE ALTERAÇÃO NA PROCEDURE PLS_OC_CTA_APLIC_VAL_LIMI_MAT

++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
	
ds_sql_w			varchar(4000);
ds_observacao_w			varchar(4000);
ds_itens_util_w			varchar(4000);

dados_limitacao_w		pls_tipos_ocor_pck.dados_limitacao;
dados_restricao_w		pls_tipos_ocor_pck.dados_restricao_select;
dados_segurado_w		pls_tipos_ocor_pck.dados_benef;
dados_proc_limitacao_w		pls_grupos_pck.t_pls_limitacao_proc_row;
dados_periodo_exec_w		pls_tipos_ocor_pck.t_datas_periodo_limit_row;
qt_permitida_w		pls_limitacao.qt_permitida%type;
nr_seq_titular_w	pls_segurado.nr_seq_titular%type;
qt_pessoas_w		integer := 0;

cursor_w			sql_pck.t_cursor;
dado_bind_w			sql_pck.t_dado_bind;

dados_tabela_aux_w		pls_tipos_ocor_pck.dados_tabela_aux_val_limit;
dados_conta_proc_w		pls_tipos_ocor_pck.dados_conta_proc;
dados_tb_selecao_w		pls_tipos_ocor_pck.dados_table_selecao_ocor;

qt_proc_util_total_w		pls_conta_proc_v.qt_procedimento%type;

qt_proc_selecao_w		integer;
i				integer;

-- Obter os beneficiários e seus dados das contas que estão na tabela de seleção. Está sendo utilizado o distinct para que seja passado uma vez apenas para cada beneficiário pois o processo

-- da validação é caro e quanto menos for executado melhor
C02 CURSOR(	nr_id_transacao_pc	pls_oc_cta_selecao_ocor_v.nr_id_transacao%type) FOR
	SELECT	distinct
		b.nr_seq_segurado,
		c.nr_seq_plano,
		c.nr_seq_contrato,
		c.nr_seq_intercambio,
		d.dt_procedimento,
		c.dt_contrato,
		c.dt_contratacao,
		c.dt_primeira_utilizacao,
		c.nr_seq_titular
	from	pls_oc_cta_selecao_ocor_v	a,
		pls_conta_ocor_v		b,
		pls_segurado_conta_v		c,
		pls_conta_proc_ocor_v		d
	where	a.nr_id_transacao	= nr_id_transacao_pc
	and	a.ie_valido		= 'S'
	and	a.ie_tipo_registro	= 'P'
	and	b.nr_sequencia		= a.nr_seq_conta
	and	c.nr_sequencia		= b.nr_seq_segurado
	and	d.nr_seq_conta		= a.nr_seq_conta
	and	d.nr_sequencia		= a.nr_seq_conta_proc;

-- Obter as limitações a qual o beneficiário está atrelado, seja pelo plano ou pelo contrato de intercâmbio. 

-- Está sendo utilizado o union e buscado todas as limitações do beneficiário para evitar replicação de código

-- e para que passe e verifique cada limitação apenas uma vez.
C03 CURSOR(	nr_seq_contrato_pc	pls_segurado_conta_v.nr_seq_contrato%type,
		nr_seq_plano_pc		pls_segurado_conta_v.nr_seq_plano%type,
		nr_seq_intercambio_pc	pls_segurado_conta_v.nr_seq_intercambio%type) FOR
	SELECT	a.nr_sequencia	nr_seq_limitacao,
		a.nr_seq_tipo_limitacao,
		a.qt_permitida,
		a.ie_periodo,
		a.qt_meses_intervalo,
		a.ie_tipo_periodo,
		b.ie_tipo_incidencia,
		coalesce(a.ie_qt_familia,'N') ie_qt_familia
	from	pls_limitacao		a,
		pls_tipo_limitacao	b
	where	a.nr_seq_contrato	= nr_seq_contrato_pc
	and	((coalesce(a.nr_seq_plano_contrato::text, '') = '') or (a.nr_seq_plano_contrato = nr_seq_plano_pc))
	and	(a.qt_permitida IS NOT NULL AND a.qt_permitida::text <> '')
	and	b.nr_sequencia		= a.nr_seq_tipo_limitacao
	and	b.ie_situacao		= 'A'
	
union

	SELECT	a.nr_sequencia	nr_seq_limitacao,
		a.nr_seq_tipo_limitacao,
		a.qt_permitida,
		a.ie_periodo,
		a.qt_meses_intervalo,
		a.ie_tipo_periodo,
		b.ie_tipo_incidencia,
		coalesce(a.ie_qt_familia,'N') ie_qt_familia
	from	pls_limitacao		a,
		pls_tipo_limitacao	b
	where	a.nr_seq_plano		= nr_seq_plano_pc
	and	(a.qt_permitida IS NOT NULL AND a.qt_permitida::text <> '')
	and	b.nr_sequencia		= a.nr_seq_tipo_limitacao
	and	b.ie_situacao		= 'A'
	
union

	select	a.nr_sequencia	nr_seq_limitacao,
		a.nr_seq_tipo_limitacao,
		a.qt_permitida,
		a.ie_periodo,
		a.qt_meses_intervalo,
		a.ie_tipo_periodo,
		b.ie_tipo_incidencia,
		coalesce(a.ie_qt_familia,'N') ie_qt_familia
	from	pls_limitacao		a,
		pls_tipo_limitacao	b
	where	a.nr_seq_intercambio	= nr_seq_intercambio_pc
	and	(a.qt_permitida IS NOT NULL AND a.qt_permitida::text <> '')
	and	b.nr_sequencia		= a.nr_seq_tipo_limitacao
	and	b.ie_situacao		= 'A';

-- Obter os procedimentos para determinado tipo de limitação. Será passado por último para os procedimentos que não estão liberados para que seja removido da tabela 

-- auxiliar os itens que não estão liberadas.
C04 CURSOR(	nr_seq_tipo_limitacao_pc	pls_limitacao.nr_seq_tipo_limitacao%type) FOR
	SELECT	a.cd_procedimento,
		a.ie_origem_proced,
		a.ie_tipo_guia,
		a.cd_doenca_cid,
		a.ie_liberado
	from	table(pls_grupos_pck.obter_procs_limitacao(nr_seq_tipo_limitacao_pc)) a
	order by a.ie_liberado desc;

-- Obter os itens que estão na tabela auxiliar. Será passado por último para os itens que estão na tabela de seleção para que seja inserida ocorrência apenas para estes itens.
C05 CURSOR(	nr_id_transacao_pc		pls_oc_cta_val_limit_aux.nr_id_transacao%type,
		nr_seq_segurado_pc		pls_oc_cta_val_limit_aux.nr_seq_segurado%type,
		nr_seq_tipo_limitacao_pc	pls_oc_cta_val_limit_aux.nr_seq_tipo_limitacao%type) FOR
	SELECT	a.nr_seq_conta,
		a.nr_seq_conta_proc,
		a.qt_item,
		a.ie_selecao,
		pls_tipos_ocor_pck.obter_sequencia_selecao(a.nr_seq_conta_proc, null, a.nr_seq_conta,
					nr_id_transacao_p, null, null, dados_regra_p.ie_excecao, 'V') nr_seq_selecao
	from	pls_oc_cta_val_limit_aux	a
	where	a.nr_id_transacao	= nr_id_transacao_pc
	and	a.nr_seq_segurado	= nr_seq_segurado_pc
	and	a.nr_seq_tipo_limitacao	= nr_seq_tipo_limitacao_pc
	and	a.ie_liberado		= 'S'
	order by a.ie_selecao;

BEGIN

-- Aqui serão buscados os beneficiários das contas que estão na tabela de seleção.
for	r_C02_w in C02(nr_id_transacao_p) loop

	-- Atualizar os dados do beneficiário atual.
	dados_segurado_w.nr_sequencia		:= r_C02_w.nr_seq_segurado;
	dados_segurado_w.nr_seq_contrato	:= r_C02_w.nr_seq_contrato;
	dados_segurado_w.nr_seq_plano		:= r_C02_w.nr_seq_plano;
	dados_segurado_w.nr_seq_intercambio	:= r_C02_w.nr_seq_intercambio;
	dados_segurado_w.dt_adesao		:= r_C02_w.dt_contratacao;
	dados_segurado_w.dt_contrato		:= r_C02_w.dt_contrato;
	dados_segurado_w.dt_primeira_utilizacao	:= r_C02_w.dt_primeira_utilizacao;
	dados_segurado_w.nr_seq_titular		:= r_C02_w.nr_seq_titular;
	
	-- Para cada beneficiário serão buscadas as limitações contratuais do plano e do contrato do beneficiário que for beneficiário da operadora, para intercâmbio

	-- será feito de outra forma pois devido ao campo ser diferente então temos mais facilidade buscando o intercâmbio em outro momento, apenas quando ele for 

	-- informado.
	for	r_C03_w in C03(r_C02_w.nr_seq_contrato, r_C02_w.nr_seq_plano, r_C02_w.nr_seq_intercambio) loop
	
		-- Obter os dados da limitação atual para realizar as consistências devidas.
		dados_limitacao_w.nr_sequencia			:= r_C03_w.nr_seq_limitacao;		
		dados_limitacao_w.nr_seq_tipo_limitacao	:= r_C03_w.nr_seq_tipo_limitacao;	
		dados_limitacao_w.qt_permitida			:= r_C03_w.qt_permitida;	
		dados_limitacao_w.ie_periodo			:= r_C03_w.ie_periodo;		
		dados_limitacao_w.qt_meses_intervalo	:= r_C03_w.qt_meses_intervalo;	
		dados_limitacao_w.ie_tipo_periodo		:= r_C03_w.ie_tipo_periodo;
		dados_limitacao_w.ie_tipo_incidencia	:= r_C03_w.ie_tipo_incidencia;
		dados_limitacao_w.ie_qt_familia			:= r_C03_w.ie_qt_familia;
		
		-- Verificar se o tipo de período informado é referente a datas.
		if (dados_limitacao_w.ie_tipo_periodo in ('D','S','M')) then
		
			-- Verificar o tipo de período informado na regra para obter o período de verificação da regra. Através das datas obtidas nesta seção serão buscados os

			-- procedimentos executados pelo benenficiário para verificar se ultrapassa a quantidade permitida pelo contrato.
			dados_periodo_exec_w := pls_tipos_ocor_pck.obter_periodo_exec_limit(	dados_limitacao_w,
												dados_segurado_w,
												r_C02_w.dt_procedimento,
												nm_usuario_p);
												
			-- Atualiza os dados da limitacao com a informação de começo e fim de período.
			dados_limitacao_w.dt_inicio_periodo	:= dados_periodo_exec_w.dt_inicio;
			dados_limitacao_w.dt_fim_periodo	:= dados_periodo_exec_w.dt_fim;
			
			-- Se não tiver encontrado as datas verifica procedimentos apenas na mesma data do procedimento.
			if (coalesce(dados_limitacao_w.dt_inicio_periodo::text, '') = '') then
				
				dados_limitacao_w.dt_inicio_periodo := trunc(r_C02_w.dt_procedimento);
			end if;
			
			if (coalesce(dados_limitacao_w.dt_fim_periodo::text, '') = '') then
			
				dados_limitacao_w.dt_fim_periodo := fim_dia(r_C02_w.dt_procedimento);
			end if;
		end if;
		
		-- Após encontrar as limitações contratuais verificar os procedimentos e\ou tipo de guia e\ou CID que cada uma delas irá bloquear o uso. 

		-- Para cada regra obtida serão buscados os registros que se encaixam para o beneficiário e somadas as quantidades utilizadas para verificar se 

		-- deve ser gerada a ocorrência ou não. Para isso será populada a tabela auxiliar com os procedimentos que foram executados e 

		-- se encaixam nos procedimentos para a limitação, após serão percorridos os registros passando para os procedimentos que não estão 

		-- na tabela de seleção e após para os que estão para que seja glosado apenas o excedentee inserir ocorrência apenas em registros da 

		-- consistência atual. Será passado inicialmente pelos procedimentos que estão liberados e posteriormente para os que não estão liberados, 

		-- para que sejam removidos. A cada loop dos procedimentos selecionados, para os procedimentos que já haviam sido selecionados anteriormente será

		-- atualizado o campo IE_LIBERADO da tabela auxiliar, para que sejam removidos os procedimentos não liberados, e ao final do processo serão 

		-- considerados apenas os procedimentos que estão com o IE_LIBERADO como 'S' na tabela de seleção.
		for	r_C04_w in C04(dados_limitacao_w.nr_seq_tipo_limitacao) loop
		
			-- Alimentar os dados da regra da limitação. Serão usados para montar a restrição do select que será montado para buscar os procedimentos 

			-- a que a regra está bloqueando.
			dados_proc_limitacao_w.cd_procedimento	:= r_C04_w.cd_procedimento;
			dados_proc_limitacao_w.ie_origem_proced	:= r_C04_w.ie_origem_proced;
			dados_proc_limitacao_w.ie_tipo_guia	:= r_C04_w.ie_tipo_guia;
			dados_proc_limitacao_w.cd_doenca_cid	:= r_C04_w.cd_doenca_cid;
			dados_proc_limitacao_w.ie_liberado	:= r_C04_w.ie_liberado;
			dado_bind_w.delete;
			
			-- Montar a restrição da regra. 
			dado_bind_w := pls_oc_cta_obter_restr_val_lim(dados_segurado_w, nr_id_transacao_p, dados_regra_p, dados_limitacao_w, 'P', dados_proc_limitacao_w, null, dado_bind_w);
			
									
			-- Só executa a verificação se alguma restrição for informada. Se estiverem todas nulas pode ser que o tipo de inicidência do tipo 

			-- de limitação não esteja informado, este campo é obrigatório e sem ele não será aplicada a validação. Se tiver uma das restrições informadas então será

			-- aplicada a informação.
			if	(not(	(coalesce(dados_restricao_w.ds_restricao_lote::text, '') = '') and (coalesce(dados_restricao_w.ds_restricao_protocolo::text, '') = '') and (coalesce(dados_restricao_w.ds_restricao_conta::text, '') = '') and (coalesce(dados_restricao_w.ds_restricao_benef::text, '') = '') and (coalesce(dados_restricao_w.ds_restricao_proc::text, '') = '') and (coalesce(dados_restricao_w.ds_restricao_mat::text, '') = ''))) then
				
				-- Montar o select que busca os procedimentos executados.
				ds_sql_w :=	'select	conta.nr_sequencia, ' || pls_tipos_ocor_pck.enter_w ||
						'	proc.nr_sequencia, ' || pls_tipos_ocor_pck.enter_w || 
						'	proc.qt_ok, ' || pls_tipos_ocor_pck.enter_w || 
						'	proc.nr_seq_proc_ref, ' || pls_tipos_ocor_pck.enter_w || 
						'	proc.ie_status ' || pls_tipos_ocor_pck.enter_w ||
						'from	pls_conta_ocor_v	conta, ' || pls_tipos_ocor_pck.enter_w || 
						'	pls_conta_proc_ocor_v	proc ' || pls_tipos_ocor_pck.enter_w || 
						'where	1 = 1 ' ||  
						dados_restricao_w.ds_restricao_conta || pls_tipos_ocor_pck.enter_w || 
						'and	proc.nr_seq_conta	= conta.nr_sequencia ' ||
						dados_restricao_w.ds_restricao_proc || pls_tipos_ocor_pck.enter_w ||
						'order by proc.dt_procedimento ';
				begin					
					-- Abrir um novo cursor
					dado_bind_w := sql_pck.executa_sql_cursor(ds_sql_w, dado_bind_w);
					
					loop
						fetch cursor_w into
							dados_conta_proc_w.nr_seq_conta, dados_conta_proc_w.nr_seq_conta_proc,
							dados_conta_proc_w.qt_procedimento, dados_conta_proc_w.nr_seq_proc_ref,
							dados_conta_proc_w.ie_status;
						EXIT WHEN NOT FOUND; /* apply on cursor_w */
					
						-- Aqui está sendo verificado se o procedimento está na tabela de seleção. Caso esteja então flega o campo

						-- IE_SELECAO da tabela auxiliar para S para que após este processo seja ordenado e verificado se 

						-- pode ser lançada a ocorrência para este procedimento ou não.
						select	count(1)
						into STRICT	qt_proc_selecao_w
						from	pls_oc_cta_selecao_ocor_v
						where	nr_id_transacao		= nr_id_transacao_p
						and	ie_valido		= 'S'
						and	ie_tipo_registro	= 'P'
						and	nr_seq_conta		= dados_conta_proc_w.nr_seq_conta
						and	nr_seq_conta_proc	= dados_conta_proc_w.nr_seq_conta_proc;
							
						-- Verificar se está na tabela de selecao para atualizar o campo IE_SELECAO da tabela auxiliar.
						if (qt_proc_selecao_w = 0) then
							
							dados_tabela_aux_w.ie_selecao := 'N';
						else
							dados_tabela_aux_w.ie_selecao := 'S';
						end if;
						
						-- Gravar os dados selecionados para gravar na tabela auxiliar. Todo o processo de gravação ou atualização será feito na procedure

						-- GERENCIA_TABELA_AUX_VAL_LIMIT da PLS_TIPOS_OCOR_PCK.
						dados_tabela_aux_w.nr_seq_segurado		:= r_C02_w.nr_seq_segurado;
						dados_tabela_aux_w.nr_seq_tipo_limitacao	:= dados_limitacao_w.nr_seq_tipo_limitacao;
						dados_tabela_aux_w.nr_id_transacao		:= nr_id_transacao_p;
						dados_tabela_aux_w.ie_liberado			:= r_C04_w.ie_liberado;
						dados_tabela_aux_w.qt_item			:= dados_conta_proc_w.qt_procedimento;
						dados_tabela_aux_w.nr_seq_conta			:= dados_conta_proc_w.nr_seq_conta;
						dados_tabela_aux_w.nr_seq_conta_proc		:= dados_conta_proc_w.nr_seq_conta_proc;
						
						pls_tipos_ocor_pck.gerencia_tabela_aux_val_limit( 'I', 'P', nr_id_transacao_p, dados_tabela_aux_w, nm_usuario_p);
					end loop; -- Contas filtradas
					close cursor_w;
				exception
				when others then
					if (cursor_w%isopen) then	
						-- Fechar os cursores que continuam abertos, os cursores que utilizam FOR - LOOP não necessitam serem fechados, serão fechados automaticamente.
						close cursor_w;
					end if;
					
					-- Insere o log na tabela e aborta a operação
					CALL pls_tipos_ocor_pck.trata_erro_sql_dinamico(dados_regra_p, ds_sql_w, nr_id_transacao_p, nm_usuario_p);
				end;
			end if;
		end loop; -- C04
				
		-- Irá gerar a ocorrência apenas quando a quantidade utilizada ultrapassar a permitida e quando o procedimento estiver na tabela de 

		-- seleção.						
		if (dados_limitacao_w.ie_qt_familia = 'S')  then
			
			select	max(nr_seq_titular)
			into STRICT	nr_seq_titular_w
			from	pls_segurado
			where	nr_sequencia	= dados_segurado_w.nr_sequencia
			and	ie_situacao_atend	= 'A'
			and	substr(pls_obter_dados_segurado(nr_sequencia,'CS'),1,10) = 'A';
			
			if (coalesce(nr_seq_titular_w::text, '') = '') then
				select	count(1)
				into STRICT	qt_pessoas_w
				from	pls_segurado
				where	nr_seq_titular	= dados_segurado_w.nr_sequencia
				and	ie_situacao_atend	= 'A'
				and	substr(pls_obter_dados_segurado(nr_sequencia,'CS'),1,10) = 'A';
				
				-- Deve ser adicionado mais 1 para que seja considerado o titular.
				qt_pessoas_w 	:= qt_pessoas_w + 1;
			else
				select	count(1)
				into STRICT	qt_pessoas_w
				from	pls_segurado
				where	nr_seq_titular	= nr_seq_titular_w
				and	ie_situacao_atend	= 'A'
				and	substr(pls_obter_dados_segurado(nr_sequencia,'CS'),1,10) = 'A';
				
				-- Deve ser adicionado mais 1 para que seja considerado o titular.
				qt_pessoas_w 	:= qt_pessoas_w + 1;
			end if;
			
			qt_permitida_w	:=	qt_pessoas_w * qt_permitida_w;
		else

			qt_permitida_w := dados_limitacao_w.qt_permitida;
		
		end if;		
				
		-- Zerar o totalizador para executar a soma dos procedimentos que foram executados pelo beneficiário.
		qt_proc_util_total_w := 0;
		ds_itens_util_w := '';
		pls_tipos_ocor_pck.limpar_nested_tables(dados_tb_selecao_w);
		i := 0;
		for	r_C05_w	in C05(	nr_id_transacao_p, r_C02_w.nr_seq_segurado,
						dados_limitacao_w.nr_seq_tipo_limitacao) loop
			
			qt_proc_util_total_w := qt_proc_util_total_w + r_C05_w.qt_item;
			
			ds_itens_util_w	:= substr(ds_itens_util_w || pls_tipos_ocor_pck.enter_w || 'Conta: ' || r_C05_w.nr_seq_conta || ' | ' || 'Procedimento: ' || r_C05_w.nr_seq_conta_proc || ' | ' || 'Quantidade: ' || r_C05_w.qt_item, 1, 4000);
						
			-- Irá gerar a ocorrência apenas quando a quantidade utilizada ultrapassar a permitida e quando o procedimento estiver na tabela de 

			-- seleção.
			if (qt_proc_util_total_w > qt_permitida_w) and (r_C05_w.ie_selecao = 'S') then
				
				ds_observacao_w	:= substr(	'O procedimento ultrapassa a quantidade permitida pelas limitações contratuais do beneficiário. ' || pls_tipos_ocor_pck.enter_w ||
								'Este beneficiário possui liberação para realizar apenas '
								|| qt_permitida_w || ' procedimento(s) neste mesmo contexto em um período de ' || dados_limitacao_w.qt_meses_intervalo || ' ' || 
								obter_valor_dominio(4171, dados_limitacao_w.ie_tipo_periodo) || pls_tipos_ocor_pck.enter_w || pls_tipos_ocor_pck.enter_w ||	
								'Procedimentos realizados no período: ' || pls_tipos_ocor_pck.enter_w ||
								ds_itens_util_w, 1, 4000);
								
				dados_tb_selecao_w.ds_seqs_selecao(i)	:= r_C05_w.nr_seq_selecao;
				dados_tb_selecao_w.ds_observacao(i)	:= ds_observacao_w;
				dados_tb_selecao_w.ie_valido(i)		:= 'S';
				
				
				-- Gravar na tabela de seleção as contas selecionadas 
				if (i = pls_cta_consistir_pck.qt_registro_transacao_w) then
				
					CALL pls_tipos_ocor_pck.gerencia_selecao_validacao(dados_tb_selecao_w.nr_seq_selecao,
						dados_tb_selecao_w.ds_seqs_selecao, 'LISTA',
						dados_tb_selecao_w.ds_observacao, dados_tb_selecao_w.ie_valido,
						nm_usuario_p);
						
					pls_tipos_ocor_pck.limpar_nested_tables(dados_tb_selecao_w);
					
					i := 0;
				else
					i := i + 1;
				end if;
			end if;
		end loop; -- C05	
		
		-- Se sobrou ainda algo manda para o banco.
		if (dados_tb_selecao_w.ds_seqs_selecao.count > 0) then
			
			CALL pls_tipos_ocor_pck.gerencia_selecao_validacao(dados_tb_selecao_w.nr_seq_selecao,
						dados_tb_selecao_w.ds_seqs_selecao, 'LISTA',
						dados_tb_selecao_w.ds_observacao, dados_tb_selecao_w.ie_valido,
						nm_usuario_p);
			
			pls_tipos_ocor_pck.limpar_nested_tables(dados_tb_selecao_w);
		end if;
		
		pls_tipos_ocor_pck.gerencia_tabela_aux_val_limit( 'T', null, nr_id_transacao_p, dados_tabela_aux_w, nm_usuario_p);
	end loop; -- C03
end loop; -- C02
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_oc_cta_aplic_val_limi_proc ( dados_regra_p pls_tipos_ocor_pck.dados_regra, nr_id_transacao_p pls_oc_cta_selecao_ocor_v.nr_id_transacao%type, nm_usuario_p usuario.nm_usuario%type) FROM PUBLIC;

