CREATE OR REPLACE PROCEDURE pls_oc_cta_tratar_val_108 ( dados_regra_p pls_tipos_ocor_pck.dados_regra, nr_id_transacao_p pls_selecao_ocor_cta.nr_id_transacao%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Validar a empresa do beneficiário suspensa/excluída
+++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
dados_tb_selecao_w	pls_tipos_ocor_pck.dados_table_selecao_ocor;
nr_idx_w		integer;
ie_gerou_w		varchar(1);
dt_suspensao_w		pls_contrato_pagador.dt_suspensao%type;
dt_reativacao_w         pls_contrato_pagador.dt_reativacao%type;

C01 CURSOR(	nr_seq_oc_cta_comb_p	dados_regra_p.nr_sequencia%type) FOR
	SELECT	a.ie_valida_empresa ie_valida
	from	pls_oc_cta_val_empres a
	where	a.nr_seq_oc_cta_comb	= nr_seq_oc_cta_comb_p;

C02 CURSOR(	nr_id_transacao_pc	pls_oc_cta_selecao_ocor_v.nr_sequencia%type,
		ie_tipo_evento_pc	pls_oc_cta_combinada.ie_evento%type) FOR
	SELECT 	x.nr_sequencia nr_seq_selecao,
		a.dt_procedimento,
		c.dt_limite_utilizacao,
		c.dt_rescisao_contrato,
		d.nr_seq_pagador
	from	pls_oc_cta_selecao_ocor_v	x,
		pls_conta_proc_v		a,
		pls_contrato			c,
		pls_segurado			d
	where	x.nr_id_transacao		= nr_id_transacao_pc
	and	a.nr_sequencia			= x.nr_seq_conta_proc
	and	a.nr_seq_contrato		= c.nr_sequencia
	and	a.nr_seq_segurado		= d.nr_sequencia;
BEGIN

if (dados_regra_p.nr_sequencia IS NOT NULL AND dados_regra_p.nr_sequencia::text <> '')  then

	CALL pls_tipos_ocor_pck.atualiza_campo_auxiliar('V', nr_id_transacao_p, null, dados_regra_p, 'S');
	pls_tipos_ocor_pck.limpar_nested_tables(dados_tb_selecao_w);
	nr_idx_w:=	0;

	for r_C01_w in C01(dados_regra_p.nr_sequencia) loop
		if (r_C01_w.ie_valida = 'S')	then
			begin
				for r_C02_w in	C02(nr_id_transacao_p, dados_regra_p.ie_evento) loop

					ie_gerou_w := 'N';
					if (r_C02_w.dt_procedimento > coalesce(r_C02_w.dt_limite_utilizacao, r_C02_w.dt_rescisao_contrato)) then
						ie_gerou_w := 'S';

						dados_tb_selecao_w.nr_seq_selecao(nr_idx_w):=	r_C02_w.nr_seq_selecao;
						dados_tb_selecao_w.ie_valido(nr_idx_w) := 'S';
						dados_tb_selecao_w.ds_observacao(nr_idx_w) :='[1] - Contrato rescindido.'||chr(13)||chr(10)||
							'Data do procedimento : ' || to_char(r_C02_w.dt_procedimento ,'dd/mm/yyyy')||
							'. Data de rescisão do contrato : '||to_char( coalesce(r_C02_w.dt_limite_utilizacao, r_C02_w.dt_rescisao_contrato),'dd/mm/yyyy');
					end if;

					if (ie_gerou_w = 'N') then
						/* 1018 - Empresa do beneficiário suspensa / excluída */

						ie_gerou_w := 'S';
						if (r_C02_w.nr_seq_pagador IS NOT NULL AND r_C02_w.nr_seq_pagador::text <> '') then

							select	trunc(dt_suspensao),
								trunc(dt_reativacao)
							into STRICT	dt_suspensao_w,
								dt_reativacao_w
							from	pls_contrato_pagador
							where	nr_sequencia = r_C02_w.nr_seq_pagador;
						end if;

						--Se existir data de suspensão do contrato e for menor que a data de execução do procedimento, então avalida ainda se não ocorreu uma
						--reativação do contrato ou se caso ocorreu,  essa data é maior( nesse caso não considera-se reativada até execução do proc)
						if	((dt_suspensao_w IS NOT NULL AND dt_suspensao_w::text <> '') and (coalesce(dt_reativacao_w::text, '') = '' or dt_reativacao_w > r_C02_w.dt_procedimento)  and (r_C02_w.dt_procedimento > dt_suspensao_w))  then
								dados_tb_selecao_w.nr_seq_selecao(nr_idx_w):=	r_C02_w.nr_seq_selecao;
								dados_tb_selecao_w.ie_valido(nr_idx_w) := 'S';
								dados_tb_selecao_w.ds_observacao(nr_idx_w) :='[2] - Pagador suspenso.'||chr(13)||chr(10)||
								'Data do procedimento : ' || to_char(r_C02_w.dt_procedimento ,'dd/mm/yyyy') ||
								'. Data de suspensão do pagador : '||to_char(dt_suspensao_w,'dd/mm/yyyy');
						else


							dados_tb_selecao_w.nr_seq_selecao(nr_idx_w):=	r_C02_w.nr_seq_selecao;
							dados_tb_selecao_w.ie_valido(nr_idx_w) := 'N';
							dados_tb_selecao_w.ds_observacao(nr_idx_w) :='';
						end if;
					end if;

					nr_idx_w := nr_idx_w + 1;
					if (nr_idx_w = pls_util_cta_pck.qt_registro_transacao_w) then
						CALL pls_tipos_ocor_pck.gerencia_selecao_validacao(	dados_tb_selecao_w.nr_seq_selecao, pls_util_cta_pck.clob_table_vazia_w,
												'SEQ', dados_tb_selecao_w.ds_observacao, dados_tb_selecao_w.ie_valido, nm_usuario_p);
						nr_idx_w := 0;
						pls_tipos_ocor_pck.limpar_nested_tables(dados_tb_selecao_w);
					end if;
				end loop;
				if (nr_idx_w > 0)	then
					CALL pls_tipos_ocor_pck.gerencia_selecao_validacao(	dados_tb_selecao_w.nr_seq_selecao, pls_util_cta_pck.clob_table_vazia_w,
											'SEQ', dados_tb_selecao_w.ds_observacao, dados_tb_selecao_w.ie_valido, nm_usuario_p);
				end if;
			exception
			when others then
				CALL pls_tipos_ocor_pck.trata_erro_sql_dinamico(dados_regra_p, sqlerrm, nr_id_transacao_p, nm_usuario_p);
			end;
			CALL pls_tipos_ocor_pck.atualiza_campo_valido('V', nr_id_transacao_p, null, dados_regra_p);
		end if;
	end loop;
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_oc_cta_tratar_val_108 ( dados_regra_p pls_tipos_ocor_pck.dados_regra, nr_id_transacao_p pls_selecao_ocor_cta.nr_id_transacao%type, nm_usuario_p usuario.nm_usuario%type) FROM PUBLIC;

