CREATE OR REPLACE PROCEDURE pls_oc_cta_tratar_val_10_imp ( nr_seq_combinada_p pls_oc_cta_combinada.nr_sequencia%type, ie_regra_excecao_p pls_oc_cta_combinada.ie_excecao%type, nr_id_transacao_p pls_oc_cta_selecao_imp.nr_id_transacao%type) AS $body$
DECLARE


_ora2pg_r RECORD;
tb_seq_selecao_w	pls_util_cta_pck.t_number_table;
tb_valido_w		pls_util_cta_pck.t_varchar2_table_1;
tb_observacao_w		pls_util_cta_pck.t_varchar2_table_4000;

-- Informações da validação de beneficiário autorização
C01 CURSOR(	nr_seq_oc_cta_comb_pc	pls_oc_cta_combinada.nr_sequencia%type) FOR
	SELECT	a.ie_numero_guia
	from	pls_oc_cta_val_benef_autor a
	where	a.nr_seq_oc_cta_comb	= nr_seq_oc_cta_comb_pc;

--Número da guia
C02 CURSOR(	nr_id_transacao_pc	pls_oc_cta_selecao_imp.nr_id_transacao%type) FOR
	SELECT	conta.nr_sequencia,
		'S' ie_valido,
		null ds_observacao
	from	pls_conta_imp conta
	where	exists (	SELECT	1
			from	pls_oc_cta_selecao_imp sel
			where	sel.nr_id_transacao = nr_id_transacao_pc
			and	sel.ie_valido = 'S'
			and	sel.nr_seq_conta = conta.nr_sequencia)
	and	exists (	select	1
			from	pls_guia_plano guia
			where  	guia.ie_status = '1'
			and    	guia.nr_seq_segurado <> conta.nr_seq_segurado_conv
			and   	guia.cd_guia = conta.cd_guia_operadora_conv);

--Número guia referência
C03 CURSOR(	nr_id_transacao_pc	pls_oc_cta_selecao_imp.nr_id_transacao%type) FOR
	SELECT	conta.nr_sequencia,
		'S' ie_valido,
		null ds_observacao
	from	pls_conta_imp conta
	where	exists (	SELECT	1
			from	pls_oc_cta_selecao_imp sel
			where	sel.nr_id_transacao = nr_id_transacao_pc
			and	sel.ie_valido = 'S'
			and	sel.nr_seq_conta = conta.nr_sequencia)
	and	exists ( select	1
			from	pls_guia_plano guia
			where  	guia.ie_status = '1'
			and    	guia.nr_seq_segurado <> conta.nr_seq_segurado_conv
			and     guia.cd_guia = conta.cd_guia_principal_conv);

--Número guia prestador
C04 CURSOR(	nr_id_transacao_pc	pls_oc_cta_selecao_imp.nr_id_transacao%type) FOR
	SELECT	conta.nr_sequencia,
		'S' ie_valido,
		null ds_observacao
	from	pls_conta_imp conta
	where	exists (	SELECT	1
			from	pls_oc_cta_selecao_imp sel
			where	sel.nr_id_transacao = nr_id_transacao_pc
			and	sel.ie_valido = 'S'
			and	sel.nr_seq_conta = conta.nr_sequencia)
	and	exists (	select	1
			from	pls_guia_plano guia
			where  	guia.ie_status = '1'
			and    	guia.nr_seq_segurado <> conta.nr_seq_segurado_conv
			and     guia.cd_guia_prestador = conta.cd_guia_prestador);

--Todos
C05 CURSOR(	nr_id_transacao_pc	pls_oc_cta_selecao_imp.nr_id_transacao%type) FOR
	SELECT	conta.nr_sequencia,
		'S' ie_valido,
		null ds_observacao
	from	pls_conta_imp conta
	where	exists (	SELECT	1
			from	pls_oc_cta_selecao_imp sel
			where	sel.nr_id_transacao = nr_id_transacao_pc
			and	sel.ie_valido = 'S'
			and	sel.nr_seq_conta = conta.nr_sequencia)
	and	exists ( select	1
			from	pls_guia_plano guia
			where  	guia.ie_status = '1'
			and    	guia.nr_seq_segurado <> conta.nr_seq_segurado_conv
			and	guia.cd_guia in (conta.cd_guia_operadora_conv, conta.cd_guia_principal_conv, conta.cd_guia_prestador));

BEGIN
-- Deve exisitr informação da regra para executar a validação
if (nr_seq_combinada_p IS NOT NULL AND nr_seq_combinada_p::text <> '') and (nr_id_transacao_p IS NOT NULL AND nr_id_transacao_p::text <> '')  then

	-- tratamento em campo auxiliar para identificar posteriormente os registros que foram alterados
	CALL pls_ocor_imp_pck.atualiza_campo_auxiliar('V', 'N', nr_id_transacao_p, null);

	--limpa as variáveis
	SELECT * FROM pls_ocor_imp_pck.limpar_nested_tables(	tb_seq_selecao_w, tb_valido_w, tb_observacao_w) INTO STRICT _ora2pg_r;
 	tb_seq_selecao_w := _ora2pg_r.tb_nr_seq_selecao_p; tb_valido_w := _ora2pg_r.tb_ie_valido_p; tb_observacao_w := _ora2pg_r.tb_ds_observacao_p;
	-- Buscar a parametrização da validação conforme cadastrado pelos usuarios.
	for	r_C01_w in C01(nr_seq_combinada_p) loop

		-- Guia
		if (r_C01_w.ie_numero_guia = 'G') then
			begin
				--Abre cursor da regra 'Número da guia' e gera ocorrência para os registros que vierem no cursor
				open C02(nr_id_transacao_p);
				loop
					fetch C02 bulk collect into	tb_seq_selecao_w, tb_valido_w, tb_observacao_w
					limit pls_util_pck.qt_registro_transacao_w;
					exit when tb_seq_selecao_w.count = 0;

					--Grava as informações na tabela de seleção
					CALL pls_ocor_imp_pck.gerencia_selecao_validacao(	tb_seq_selecao_w, tb_valido_w,
											tb_observacao_w, nr_id_transacao_p,
											'SEQ_CONTA');
					--limpa as variáveis
					SELECT * FROM pls_ocor_imp_pck.limpar_nested_tables(	tb_seq_selecao_w, tb_valido_w, tb_observacao_w) INTO STRICT _ora2pg_r;
 	tb_seq_selecao_w := _ora2pg_r.tb_nr_seq_selecao_p; tb_valido_w := _ora2pg_r.tb_ie_valido_p; tb_observacao_w := _ora2pg_r.tb_ds_observacao_p;
				end loop;
				close C02;
			exception
			when others then
				--Fecha cursor
				if (C02%isopen) then
					close C02;
				end if;
			end;
		-- Guia de referência
		elsif (r_C01_w.ie_numero_guia = 'R') then
			begin
				--Abre cursor da regra 'Número guia referência' e gera ocorrência para os registros que vierem no cursor
				open C03(nr_id_transacao_p);
				loop
					fetch C03 bulk collect into	tb_seq_selecao_w, tb_valido_w, tb_observacao_w
					limit pls_util_pck.qt_registro_transacao_w;
					exit when tb_seq_selecao_w.count = 0;

					--Grava as informações na tabela de seleção
					CALL pls_ocor_imp_pck.gerencia_selecao_validacao(	tb_seq_selecao_w, tb_valido_w,
											tb_observacao_w, nr_id_transacao_p,
											'SEQ_CONTA');
					--limpa as variáveis
					SELECT * FROM pls_ocor_imp_pck.limpar_nested_tables(	tb_seq_selecao_w, tb_valido_w, tb_observacao_w) INTO STRICT _ora2pg_r;
 	tb_seq_selecao_w := _ora2pg_r.tb_nr_seq_selecao_p; tb_valido_w := _ora2pg_r.tb_ie_valido_p; tb_observacao_w := _ora2pg_r.tb_ds_observacao_p;
				end loop;
				close C03;
			exception
			when others then
				--Fecha cursor
				if (C03%isopen) then
					close C03;
				end if;
			end;
		-- Guia prestador
		elsif (r_C01_w.ie_numero_guia = 'P') then
			begin
				--Abre cursor da regra 'Número guia prestador' e gera ocorrência para os registros que vierem no cursor
				open C04(nr_id_transacao_p);
				loop
					fetch C04 bulk collect into	tb_seq_selecao_w, tb_valido_w, tb_observacao_w
					limit pls_util_pck.qt_registro_transacao_w;
					exit when tb_seq_selecao_w.count = 0;

					--Grava as informações na tabela de seleção
					CALL pls_ocor_imp_pck.gerencia_selecao_validacao(	tb_seq_selecao_w, tb_valido_w,
											tb_observacao_w, nr_id_transacao_p,
											'SEQ_CONTA');
					--limpa as variáveis
					SELECT * FROM pls_ocor_imp_pck.limpar_nested_tables(	tb_seq_selecao_w, tb_valido_w, tb_observacao_w) INTO STRICT _ora2pg_r;
 	tb_seq_selecao_w := _ora2pg_r.tb_nr_seq_selecao_p; tb_valido_w := _ora2pg_r.tb_ie_valido_p; tb_observacao_w := _ora2pg_r.tb_ds_observacao_p;
				end loop;
				close C04;
			exception
			when others then
				--Fecha cursor
				if (C04%isopen) then
					close C04;
				end if;
			end;
		-- Guia ou guia de referencia (Ambos)
		elsif (r_C01_w.ie_numero_guia = 'A') then
			begin
				--Abre cursor da regra 'Todos' e gera ocorrência para os registros que vierem no cursor
				open C05(nr_id_transacao_p);
				loop
					fetch C05 bulk collect into	tb_seq_selecao_w, tb_valido_w, tb_observacao_w
					limit pls_util_pck.qt_registro_transacao_w;
					exit when tb_seq_selecao_w.count = 0;

					--Grava as informações na tabela de seleção
					CALL pls_ocor_imp_pck.gerencia_selecao_validacao(	tb_seq_selecao_w, tb_valido_w,
											tb_observacao_w, nr_id_transacao_p,
											'SEQ_CONTA');
					--limpa as variáveis
					SELECT * FROM pls_ocor_imp_pck.limpar_nested_tables(	tb_seq_selecao_w, tb_valido_w, tb_observacao_w) INTO STRICT _ora2pg_r;
 	tb_seq_selecao_w := _ora2pg_r.tb_nr_seq_selecao_p; tb_valido_w := _ora2pg_r.tb_ie_valido_p; tb_observacao_w := _ora2pg_r.tb_ds_observacao_p;
				end loop;
				close C05;
			exception
			when others then
				--Fecha cursor
				if (C05%isopen) then
					close C05;
				end if;
			end;
		end if;

	end loop; -- C01
	--Se tiver alguma informaçõa grava na tabela
	CALL pls_ocor_imp_pck.gerencia_selecao_validacao(	tb_seq_selecao_w, tb_valido_w,
							tb_observacao_w, nr_id_transacao_p,
							'SEQ_CONTA');
	-- seta os registros que serão válidos ou inválidos após o processamento
	CALL pls_ocor_imp_pck.atualiza_campo_valido('V', 'N',
						ie_regra_excecao_p, null,
						nr_id_transacao_p, null);
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
