SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_oc_cta_tratar_val_112 ( dados_regra_p pls_tipos_ocor_pck.dados_regra, nr_id_transacao_p pls_oc_cta_selecao_ocor_v.nr_id_transacao%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


tb_seq_selecao_w		dbms_sql.number_table;
tb_valido_w			dbms_sql.varchar2_table;
tb_observacao_w			dbms_sql.varchar2_table;
ds_observacao_1_w		varchar(255);

-- Informações da validação guia principal autorização
C01 CURSOR(	nr_seq_oc_cta_comb_pc	pls_oc_cta_combinada.nr_sequencia%type) FOR
	SELECT	ie_valida_cobertura
	from	pls_oc_cta_val_cobertura a
	where	a.nr_seq_oc_cta_comb	= nr_seq_oc_cta_comb_pc;

C02 CURSOR(	nr_id_transacao_pc	pls_oc_cta_selecao_ocor_v.nr_id_transacao%type,
		ds_observacao_pc	text) FOR
	SELECT	sel.nr_sequencia nr_seq_selecao,
		'S' ie_valido,
		ds_observacao_pc
	from	pls_oc_cta_selecao_imp	sel,
		pls_conta_proc		proc
	where	sel.nr_id_transacao	= nr_id_transacao_pc
	and	sel.ie_valido		= 'S'
	and	proc.nr_seq_conta	= sel.nr_seq_conta
	and	proc.nr_sequencia	= sel.nr_seq_conta_proc
	and 	coalesce(proc.nr_seq_cobertura, 0)  = 0;

BEGIN

-- Deve existir informação da regra para aplicar a validação
if (dados_regra_p.nr_sequencia IS NOT NULL AND dados_regra_p.nr_sequencia::text <> '') then

	--Procedimento não possuí cobertura
	ds_observacao_1_w:= wheb_mensagem_pck.get_texto(802786);

	for	r_C01_w in C01(dados_regra_p.nr_sequencia) loop

		-- tratamento em campo auxiliar para identificar posteriormente os registros que foram alterados
		CALL pls_tipos_ocor_pck.atualiza_campo_auxiliar(	'V',
								nr_id_transacao_p,
								null,
								dados_regra_p);

		if (r_C01_w.ie_valida_cobertura = 'S') then

			tb_seq_selecao_w	:= pls_tipos_ocor_pck.num_table_vazia;
			tb_valido_w		:= pls_tipos_ocor_pck.vchr2_table_vazia;
			tb_observacao_w		:= pls_tipos_ocor_pck.vchr2_table_vazia;

			open C02(nr_id_transacao_p, ds_observacao_1_w);
			loop
				fetch C02 bulk collect into	tb_seq_selecao_w, tb_valido_w, tb_observacao_w
				limit pls_util_pck.qt_registro_transacao_w;
				exit when tb_seq_selecao_w.count = 0;

				--Grava as informações na tabela de seleção
				CALL pls_tipos_ocor_pck.gerencia_selecao_validacao(	tb_seq_selecao_w,
										pls_tipos_ocor_pck.clob_table_vazia,
										'SEQ',
										tb_observacao_w,
										tb_valido_w,
										nm_usuario_p);

				--limpa as variáveis
				tb_valido_w		:= pls_tipos_ocor_pck.vchr2_table_vazia;
				tb_seq_selecao_w	:= pls_tipos_ocor_pck.num_table_vazia;

			end loop;
			close C02;

			if (tb_seq_selecao_w.count > 0) then
				-- Será passado uma lista com todas a sequencias da seleção para a conta e para seus itens, estas sequências serão atualizadas com os mesmos dados da conta,
				-- conforme passado por parâmetro,
				CALL pls_tipos_ocor_pck.gerencia_selecao_validacao(	tb_seq_selecao_w,
										pls_tipos_ocor_pck.clob_table_vazia,
										'SEQ',
										tb_observacao_w,
										tb_valido_w,
										nm_usuario_p);

			end if;

		end if;

		-- seta os registros que serão válidos ou inválidos após o processamento
		CALL pls_tipos_ocor_pck.atualiza_campo_valido(	'V',
								nr_id_transacao_p,
								null,
								dados_regra_p);

	end loop;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_oc_cta_tratar_val_112 ( dados_regra_p pls_tipos_ocor_pck.dados_regra, nr_id_transacao_p pls_oc_cta_selecao_ocor_v.nr_id_transacao%type, nm_usuario_p usuario.nm_usuario%type) FROM PUBLIC;

