SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_oc_cta_tratar_val_12_imp ( nr_seq_combinada_p pls_oc_cta_combinada.nr_sequencia%type, ie_regra_excecao_p pls_oc_cta_combinada.ie_excecao%type, nr_id_transacao_p pls_oc_cta_selecao_imp.nr_id_transacao%type) AS $body$
DECLARE


_ora2pg_r RECORD;
ie_registro_valido_w	varchar(1);
nr_index_sel_w		integer;
tb_seq_selecao_w	pls_util_cta_pck.t_number_table;
tb_valido_w		pls_util_cta_pck.t_varchar2_table_1;
tb_observacao_w		pls_util_cta_pck.t_varchar2_table_4000;

-- Informações da validação de restrição do material
C01 CURSOR(	nr_seq_oc_cta_comb_pc	pls_oc_cta_combinada.nr_sequencia%type) FOR
	SELECT	a.ie_consiste_sexo,
		a.ie_consiste_idade,
		a.ie_consiste_qtd,
		a.ie_bloqueio_mat
	from	pls_oc_cta_val_restr_mat a
	where	a.nr_seq_oc_cta_comb	= nr_seq_oc_cta_comb_pc;

--Cursor principal das contas e registros selecionados
C02 CURSOR(	nr_id_transacao_pc	pls_oc_cta_selecao_imp.nr_id_transacao%type) FOR
	SELECT	sel.nr_sequencia,
		regra.qt_minima,
		regra.qt_maxima,
		regra.ie_sexo_exclusivo,
		regra.qt_idade_minima,
		regra.qt_idade_maxima,
		regra.ie_bloqueia_custo_op,
		regra.ie_bloqueia_intercambio,
		regra.ie_bloqueia_prod_nao_reg,
		regra.ie_bloqueia_prod_reg,
		regra.ie_bloqueia_pre_pag,
		mat.qt_executado,
		conta.ie_sexo_conv,
		conta.qt_idade_conta_anos_conv,
		plano.ie_preco,
		benef.nr_seq_intercambio,
		plano.ie_regulamentacao
	from	pls_oc_cta_selecao_imp sel,
		pls_conta_mat_imp mat,
		pls_conta_imp conta,
		pls_segurado benef,
		pls_plano plano,
		pls_material_restricao regra
	where	sel.nr_id_transacao 	= nr_id_transacao_pc
	and	sel.ie_valido 		= 'S'
	and	conta.nr_sequencia	= sel.nr_seq_conta
	and	benef.nr_sequencia	= conta.nr_seq_segurado_conv
	and	plano.nr_sequencia	= benef.nr_seq_plano
	and	mat.nr_sequencia	= sel.nr_seq_conta_mat
	and	regra.nr_seq_material	= mat.nr_seq_material_conv
	and	mat.dt_execucao_conv between regra.dt_inicio_vigencia_ref and regra.dt_fim_vigencia_ref;
BEGIN

-- Deve exisitr informação da regra para executar a validação
if (nr_seq_combinada_p IS NOT NULL AND nr_seq_combinada_p::text <> '') and (nr_id_transacao_p IS NOT NULL AND nr_id_transacao_p::text <> '')  then

	-- tratamento em campo auxiliar para identificar posteriormente os registros que foram alterados
	CALL pls_ocor_imp_pck.atualiza_campo_auxiliar('V', 'N', nr_id_transacao_p, null);

	--limpa as variáveis
	SELECT * FROM pls_ocor_imp_pck.limpar_nested_tables(	tb_seq_selecao_w, tb_valido_w, tb_observacao_w) INTO STRICT _ora2pg_r;
 	tb_seq_selecao_w := _ora2pg_r.tb_nr_seq_selecao_p; tb_valido_w := _ora2pg_r.tb_ie_valido_p; tb_observacao_w := _ora2pg_r.tb_ds_observacao_p;
	nr_index_sel_w := 0;

	for	r_C01_w in C01(nr_seq_combinada_p) loop
		for	r_C02_w in C02(nr_id_transacao_p) loop

			ie_registro_valido_w := 'A';

			--Consiste Quantidade
			if (r_C01_w.ie_consiste_qtd = 'S' and ie_registro_valido_w != 'N') then
				-- Esta retrição irá encontrar os materiais da conta que não estão de acordo com as quantidades informadas na regra da pasta "Restrição".
				-- Sempre será verificada a quantidade apresentada do material.
				if	((r_C02_w.qt_executado < r_C02_w.qt_minima) or (r_C02_w.qt_executado > r_C02_w.qt_maxima)) then
					ie_registro_valido_w := 'S';
				else
					ie_registro_valido_w := 'N';
				end if;
			end if;

			--Consiste Sexo
			if (r_C01_w.ie_consiste_sexo = 'S' and ie_registro_valido_w != 'N') then
				-- Esta restrição verifica o sexo do beneficiário da conta.
				-- A restrição de material por sexo indica que somente pessoas daquele sexo  podem usar o determinado material.
				if (r_C02_w.ie_sexo_conv != r_C02_w.ie_sexo_exclusivo) then
					ie_registro_valido_w := 'S';
				else
					ie_registro_valido_w := 'N';
				end if;
			end if;

			--Consiste idade
			if (r_C01_w.ie_consiste_idade = 'S' and ie_registro_valido_w != 'N') then
				--Idade mínima e idade máxima da regra comparada à idade do segurado
				if	((r_C02_w.qt_idade_conta_anos_conv < r_C02_w.qt_idade_minima) or (r_C02_w.qt_idade_conta_anos_conv > r_C02_w.qt_idade_maxima)) then
					ie_registro_valido_w := 'S';
				else
					ie_registro_valido_w := 'N';
				end if;
			end if;

			-- Se for para consistir o bloqueio do material será montado a restrição conforme o bloqueio selecionado abaixo.
			if (r_C01_w.ie_bloqueio_mat IS NOT NULL AND r_C01_w.ie_bloqueio_mat::text <> '') then

				-- O bloqueio do material indica que beneficiário com certas  características contratuais não podem utilizar determinado material.
				-- Bloqueia uso contratos custo operacional
				if (r_C01_w.ie_bloqueio_mat = 'BCO' and ie_registro_valido_w != 'N') then
					-- Se é para verificar o bloqueio por custo operacional então  a ocorrência deve ser gerada quando o produto do beneficiário
					-- detém a formação de preço 3 - Pós estabelecido por custo operacional e a restrição do material indicar que o mesmo está
					-- bloqueado para o uso por este tipo de formação de preço.
					if (r_C02_w.ie_preco = '3' and r_C02_w.ie_bloqueia_custo_op = 'S') then
						ie_registro_valido_w := 'S';
					else
						ie_registro_valido_w := 'N';
					end if;
				-- Bloqueia uso intercâmbio
				elsif (r_C01_w.ie_bloqueio_mat = 'BI' and ie_registro_valido_w != 'N') then
					-- Se é para verificar o bloquei para uso do contrato de intercâmbio então a ocorrência deve ser gerada quando o beneficiário
					-- tiver informação de contrato de intercâmbio e a restrição do material indicar que o uso por contratos de intercâmbio estiver bloqueada.
					if ((r_C02_w.nr_seq_intercambio IS NOT NULL AND r_C02_w.nr_seq_intercambio::text <> '') and r_C02_w.ie_bloqueia_intercambio = 'S') then
						ie_registro_valido_w := 'S';
					else
						ie_registro_valido_w := 'N';
					end if;
				-- Bloqueia uso plano não regulamentado
				elsif (r_C01_w.ie_bloqueio_mat = 'BNR' and ie_registro_valido_w != 'N') then
					-- Se é para verificar o bloqueio por produto não regulamentado então deve ser gerada a ocorrência quando o produto do beneficiário
					-- seja pré regulamentado e a restrição do material inidicar que o mesmo está bloqueado para uso por esses planos
					if (r_C02_w.ie_regulamentacao = 'R' and r_C02_w.ie_bloqueia_prod_nao_reg = 'S') then
						ie_registro_valido_w := 'S';
					else
						ie_registro_valido_w := 'N';
					end if;
				-- Bloqueia uso contratos pré-pagamento
				elsif (r_C01_w.ie_bloqueio_mat = 'BPP' and ie_registro_valido_w != 'N') then
					-- Se é para verificar o bloqueio de pré pagamento então a ocorrência deve ser gerada quando a formação de preço do produto
					-- do beneficiário for 1 - Pré-estabelecido e a restrição do material indicar que o uso do material para este tipo de produto
					-- está bloqueada.
					if (r_C02_w.ie_preco = '1' and r_C02_w.ie_bloqueia_pre_pag = 'S') then
						ie_registro_valido_w := 'S';
					else
						ie_registro_valido_w := 'N';
					end if;
				elsif (r_C01_w.ie_bloqueio_mat = 'BR' and ie_registro_valido_w != 'N') then
					-- Se é para verificar o bloqueio por produto regulamentado então deve ser gerada a ocorrência quando o produto do beneficiário
					-- seja pós-regulamentado ou adaptado e a restrição do material inidicar que o mesmo está bloqueado para uso por esses planos
					if (r_C02_w.ie_regulamentacao in ('P','A') and r_C02_w.ie_bloqueia_prod_reg = 'S') then
						ie_registro_valido_w := 'S';
					else
						ie_registro_valido_w := 'N';
					end if;
				end if;

			end if;

			if (ie_registro_valido_w = 'S') then

				tb_seq_selecao_w(nr_index_sel_w) := r_C02_w.nr_sequencia;
				tb_observacao_w(nr_index_sel_w)  := null;
				tb_valido_w(nr_index_sel_w) 	 := 'S';

				if (nr_index_sel_w >= pls_cta_consistir_pck.qt_registro_transacao_w ) then

					--Grava o que restar nas variáveis na tabela
					CALL pls_ocor_imp_pck.gerencia_selecao_validacao(	tb_seq_selecao_w, tb_valido_w,
											tb_observacao_w, nr_id_transacao_p,
											'SEQ');
					--limpa as variáveis
					SELECT * FROM pls_ocor_imp_pck.limpar_nested_tables(	tb_seq_selecao_w, tb_valido_w, tb_observacao_w) INTO STRICT _ora2pg_r;
 	tb_seq_selecao_w := _ora2pg_r.tb_nr_seq_selecao_p; tb_valido_w := _ora2pg_r.tb_ie_valido_p; tb_observacao_w := _ora2pg_r.tb_ds_observacao_p;

					nr_index_sel_w		:= 0;
				else
					nr_index_sel_w := nr_index_sel_w + 1;
				end if;
			end if;

		end loop; --C02
	end loop; -- C01
	--Grava o que restar nas variáveis na tabela
	CALL pls_ocor_imp_pck.gerencia_selecao_validacao(	tb_seq_selecao_w, tb_valido_w,
							tb_observacao_w, nr_id_transacao_p,
							'SEQ');

	-- seta os registros que serão válidos ou inválidos após o processamento
	CALL pls_ocor_imp_pck.atualiza_campo_valido('V', 'N',
						ie_regra_excecao_p, null,
						nr_id_transacao_p, null);
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_oc_cta_tratar_val_12_imp ( nr_seq_combinada_p pls_oc_cta_combinada.nr_sequencia%type, ie_regra_excecao_p pls_oc_cta_combinada.ie_excecao%type, nr_id_transacao_p pls_oc_cta_selecao_imp.nr_id_transacao%type) FROM PUBLIC;

