CREATE OR REPLACE PROCEDURE pls_oc_cta_tratar_val_14_imp ( nr_seq_combinada_p pls_oc_cta_combinada.nr_sequencia%type, ie_regra_excecao_p pls_oc_cta_combinada.ie_excecao%type, nr_id_transacao_p pls_oc_cta_selecao_imp.nr_id_transacao%type) AS $body$
DECLARE


_ora2pg_r RECORD;
tb_seq_selecao_w				pls_util_cta_pck.t_number_table;
tb_valido_w					pls_util_cta_pck.t_varchar2_table_1;
tb_observacao_w					pls_util_cta_pck.t_varchar2_table_4000;

-- Informações da regra de validação da carteira
C01 CURSOR(	nr_seq_oc_cta_comb_pc		pls_oc_cta_combinada.nr_sequencia%type) FOR
	SELECT	ie_valida
	from	pls_oc_cta_val_pacote
	where	nr_seq_oc_cta_comb		= nr_seq_oc_cta_comb_pc;

-- Procedimentos que foram aplicados nos filtros da regra
C02 CURSOR(	nr_id_transacao_pc		pls_oc_cta_selecao_imp.nr_id_transacao%type) FOR
	SELECT	proc.nr_seq_conta,
		acomod.nr_seq_composicao
	from	pls_oc_cta_selecao_imp		sel,
		pls_conta_proc_imp		proc,
		pls_pacote_tipo_acomodacao	acomod
	where	sel.nr_id_transacao		= nr_id_transacao_pc
	and	sel.ie_valido			= 'S'
	and	proc.nr_sequencia		= sel.nr_seq_conta_proc
	and	proc.ie_tipo_despesa_conv	= '4'
	and	acomod.nr_sequencia		= proc.nr_seq_pacote_conv;

--Verifica os procedimento/materiais existentes na conta que não são pacotes porém estão contidos na composição do pacote existente na conta
C03 CURSOR(	nr_id_transacao_pc		pls_oc_cta_selecao_ocor_v.nr_id_transacao%type,
		nr_seq_conta_pc			pls_conta.nr_sequencia%type,
		nr_seq_composicao_pc		pls_pacote_composicao.nr_sequencia%type) FOR
	SELECT	sel.nr_sequencia,
		'S' ie_valido,
		null ds_observacao
	from	pls_oc_cta_selecao_imp		sel,
		pls_conta_proc_imp		proc
	where	sel.nr_id_transacao		= nr_id_transacao_pc
	and	sel.ie_valido			= 'S'
	and	sel.nr_seq_conta		= nr_seq_conta_pc
	and	proc.nr_sequencia		= sel.nr_seq_conta_proc
	and	proc.ie_tipo_despesa_conv	<> '4'
	and	exists (   SELECT	1
			    from	pls_pacote_procedimento	pp,
					pls_pacote_composicao	pc
			    where	pp.nr_seq_composicao	= nr_seq_composicao_pc
			    and		pp.cd_procedimento	= proc.cd_procedimento_conv
			    and		pp.ie_origem_proced	= proc.ie_origem_proced_conv
			    and		pp.ie_situacao		= 'A'
			    and		pc.nr_sequencia		= pp.nr_seq_composicao
			    and		pc.ie_situacao		= 'A'
			
union all

			    select	1
			    from	pls_pacote_composicao	pc,
					pls_pacote_procedimento	pp
			    where	pc.nr_sequencia		= nr_seq_composicao_pc
			    and		pc.ie_situacao		= 'A'
			    and		pp.nr_seq_composicao	= pc.nr_sequencia
			    and		(pp.nr_seq_grupo_servico IS NOT NULL AND pp.nr_seq_grupo_servico::text <> '')
			    and		pls_se_grupo_preco_servico(pp.nr_seq_grupo_servico, proc.cd_procedimento_conv, proc.ie_origem_proced_conv) = 'S'
			    and		pp.ie_situacao		= 'A'
			  )
	
union all

	select	sel.nr_sequencia,
		'S' ie_valido,
		null ds_observacao
	from	pls_oc_cta_selecao_imp		sel,
		pls_conta_mat_imp		mat
	where	sel.nr_id_transacao		= nr_id_transacao_pc
	and	sel.ie_valido			= 'S'
	and	sel.nr_seq_conta		= nr_seq_conta_pc
	and	mat.nr_sequencia		= sel.nr_seq_conta_mat
	and	exists (   select	1
			    from	pls_pacote_material	pm,
					pls_pacote_composicao	pc
			    where	pm.nr_seq_composicao	= nr_seq_composicao_pc
			    and		pm.nr_seq_material	= mat.nr_seq_material_conv
			    and		pm.ie_situacao		= 'A'
			    and		pc.nr_sequencia		= pm.nr_seq_composicao
			    and		pc.ie_situacao		= 'A'
			);
BEGIN
-- Deve existir a informação da regra e transação para aplicar a validação
if (nr_seq_combinada_p IS NOT NULL AND nr_seq_combinada_p::text <> '') and (nr_id_transacao_p IS NOT NULL AND nr_id_transacao_p::text <> '')  then
	-- Buscar os parâmteros da validação cadastrados pelo usuário
	for r_C01_w in C01(nr_seq_combinada_p) loop
		-- Verifica se é para validar a regra.
		if (r_C01_w.ie_valida = 'S') then
			-- tratamento em campo auxiliar para identificar posteriormente os registros que foram alterados
			CALL pls_ocor_imp_pck.atualiza_campo_auxiliar('V', 'N', nr_id_transacao_p, null);
			--limpa as variáveis
			SELECT * FROM pls_ocor_imp_pck.limpar_nested_tables(	tb_seq_selecao_w, tb_valido_w, tb_observacao_w) INTO STRICT _ora2pg_r;
 	tb_seq_selecao_w := _ora2pg_r.tb_nr_seq_selecao_p; tb_valido_w := _ora2pg_r.tb_ie_valido_p; tb_observacao_w := _ora2pg_r.tb_ds_observacao_p;
			-- Buscar os pacotes lançados para as contas da geração da ocorrência.
			for r_C02_w in C02(nr_id_transacao_p) loop
				begin
					-- Aqui busca os procedimentos que deveriam estar somente dentro do pacote e estão duplicadaos na conta.
					open C03(nr_id_transacao_p, r_C02_w.nr_seq_conta, r_C02_w.nr_seq_composicao);
					loop
						fetch C03 bulk collect into tb_seq_selecao_w, tb_valido_w, tb_observacao_w
						limit pls_util_cta_pck.qt_registro_transacao_w;
						exit when tb_seq_selecao_w.count = 0;
						--Grava as informações na tabela de seleção
						CALL pls_ocor_imp_pck.gerencia_selecao_validacao(	tb_seq_selecao_w, tb_valido_w,
												tb_observacao_w, nr_id_transacao_p,
												'SEQ');
						--limpa as variáveis
						SELECT * FROM pls_ocor_imp_pck.limpar_nested_tables(	tb_seq_selecao_w, tb_valido_w, tb_observacao_w) INTO STRICT _ora2pg_r;
 	tb_seq_selecao_w := _ora2pg_r.tb_nr_seq_selecao_p; tb_valido_w := _ora2pg_r.tb_ie_valido_p; tb_observacao_w := _ora2pg_r.tb_ds_observacao_p;
					end loop;
					close C03;
				exception
				when others then
					--Fecha cursor
					if (C03%isopen) then

						close C03;
					end if;
				end;
			end loop;--C02
		end if;
	end loop;--C01
	--Grava o que restar nas variáveis na tabela
	CALL pls_ocor_imp_pck.gerencia_selecao_validacao(	tb_seq_selecao_w, tb_valido_w,
							tb_observacao_w, nr_id_transacao_p,
							'SEQ');
	-- seta os registros que serão válidos ou inválidos após o processamento
	CALL pls_ocor_imp_pck.atualiza_campo_valido(	'V', 'N',
							ie_regra_excecao_p, null,
							nr_id_transacao_p, null);
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
