CREATE OR REPLACE PROCEDURE pls_oc_cta_tratar_val_14 ( dados_regra_p pls_tipos_ocor_pck.dados_regra, nr_id_transacao_p pls_selecao_ocor_cta.nr_id_transacao%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: 	Irá realizar a chamada para a rotina de validação conforme a validação selecionada na regra.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[ X]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_seq_selecao_w		dbms_sql.number_table;
ds_observacao_w			dbms_sql.varchar2_table;
ie_valido_w			dbms_sql.varchar2_table;

-- Procedimentos que foram aplicados nos filtros da regra
C01 CURSOR(	nr_id_transacao_pc	pls_oc_cta_selecao_ocor_v.nr_id_transacao%type) FOR
	SELECT	a.nr_seq_conta,
		b.nr_seq_composicao
	from	pls_oc_cta_selecao_ocor_v		x,
		pls_conta_proc_ocor_v			a,
		pls_pacote_tipo_acomodacao		b
	where	x.nr_id_transacao	= nr_id_transacao_pc
	and	x.ie_valido		= 'S'
	and	a.nr_sequencia		= x.nr_seq_conta_proc
	and	a.ie_tipo_despesa	= '4'
	and	b.nr_sequencia		= a.nr_seq_preco_pacote;

--Verifica os procedimento/materiais existentes na conta que não são pacotes porém estão contidos na composição do pacote existente na conta
C02 CURSOR(	nr_id_transacao_pc	pls_oc_cta_selecao_ocor_v.nr_id_transacao%type,
		nr_seq_conta_pc		pls_conta.nr_sequencia%type,
		nr_seq_composicao_pc	pls_pacote_composicao.nr_sequencia%type) FOR
	SELECT	x.nr_sequencia nr_seq_selecao,
		'S' ie_valido,
		null ds_observacao
	from	pls_oc_cta_selecao_ocor_v	x,
		pls_conta_proc_ocor_v		a
	where	x.nr_id_transacao	= nr_id_transacao_pc
	and	x.ie_valido		= 'S'
	and	x.nr_seq_conta		= nr_seq_conta_pc
	and	a.nr_sequencia		= x.nr_seq_conta_proc
	and	a.ie_tipo_despesa	<> '4'
	and	exists (   SELECT	1
			    from	pls_pacote_composicao	pc,
					pls_pacote_procedimento	pp
			    where	pc.nr_sequencia		= nr_seq_composicao_pc
			    and		pc.ie_situacao		= 'A'
			    and		pp.nr_seq_composicao	= pc.nr_sequencia
			    and		pp.cd_procedimento	= a.cd_procedimento
			    and		pp.ie_origem_proced	= a.ie_origem_proced
			    and		pp.ie_situacao		= 'A'
			
union all

			    select	1
			    from	pls_pacote_composicao	pc,
					pls_pacote_procedimento	pp
			    where	pc.nr_sequencia		= nr_seq_composicao_pc
			    and		pc.ie_situacao		= 'A'
			    and		pp.nr_seq_composicao	= pc.nr_sequencia
			    and		(pp.nr_seq_grupo_servico IS NOT NULL AND pp.nr_seq_grupo_servico::text <> '')
			    and		pls_se_grupo_preco_servico(pp.nr_seq_grupo_servico, a.cd_procedimento, a.ie_origem_proced) = 'S'
			    and		pp.ie_situacao		= 'A')
	
union all

	select	x.nr_sequencia nr_seq_selecao,
		'S' ie_valido,
		null ds_observacao
	from	pls_oc_cta_selecao_ocor_v	x,
		pls_conta_mat_ocor_v		a
	where	x.nr_id_transacao	= nr_id_transacao_pc
	and	x.ie_valido		= 'S'
	and	x.nr_seq_conta		= nr_seq_conta_pc
	and	a.nr_sequencia		= x.nr_seq_conta_mat
	and	exists (   select	1
			    from	pls_pacote_composicao	pc,
					pls_pacote_material	pm
			    where	pc.nr_sequencia		= nr_seq_composicao_pc
			    and		pc.ie_situacao		= 'A'
			    and		pm.nr_seq_composicao	= pc.nr_sequencia
			    and		pm.nr_seq_material	= a.nr_seq_material
			    and		pm.ie_situacao		= 'A');

-- Informações da regra de validação da carteira
C03 CURSOR(	nr_seq_oc_cta_comb_pc	dados_regra_p.nr_sequencia%type) FOR
	SELECT	a.ie_valida
	from	pls_oc_cta_val_pacote a
	where	a.nr_seq_oc_cta_comb	= nr_seq_oc_cta_comb_pc;
BEGIN
-- Se não tiver informação da regra nem procura pelos parâmetros da validação
if (dados_regra_p.nr_sequencia IS NOT NULL AND dados_regra_p.nr_sequencia::text <> '') then
	-- Buscar os parâmteros da validação cadastrados pelo usuário
	for r_c03_w in C03(dados_regra_p.nr_sequencia) loop
		-- Verifica se é para validar a regra.
		if (r_c03_w.ie_valida = 'S') then
			-- tratamento em campo auxiliar para identificar posteriormente os registros que foram alterados
			CALL pls_tipos_ocor_pck.atualiza_campo_auxiliar('V', nr_id_transacao_p, null, dados_regra_p);
			-- Buscar os pacotes lançados para as contas da geração da ocorrência.
			for r_c01_w in C01(nr_id_transacao_p) loop
				begin
					-- Aqui busca os procedimentos que deveriam estar somente dentro do pacote e estão duplicadaos na conta.
					open C02(nr_id_transacao_p, r_c01_w.nr_seq_conta, r_c01_w.nr_seq_composicao);
					loop
					nr_seq_selecao_w	:= pls_util_cta_pck.num_table_vazia_w;
					ie_valido_w		:= pls_util_cta_pck.vchr2_table_vazia_w;
					ds_observacao_w		:= pls_util_cta_pck.vchr2_table_vazia_w;
					fetch C02 bulk collect into nr_seq_selecao_w, ie_valido_w, ds_observacao_w limit pls_util_cta_pck.qt_registro_transacao_w;
					exit when nr_seq_selecao_w.count = 0;
						begin
						CALL pls_tipos_ocor_pck.gerencia_selecao_validacao(	nr_seq_selecao_w, pls_util_cta_pck.clob_table_vazia_w,
												'SEQ', ds_observacao_w, ie_valido_w, nm_usuario_p);
						end;
					end loop;
					close C02;
				exception
				when others then
					-- Tratar erro gerado no sql dinâmico, será inserido registro no log e abortado o processo exibindo mensagem de erro.
					CALL pls_tipos_ocor_pck.trata_erro_sql_dinamico(dados_regra_p, sqlerrm, nr_id_transacao_p, nm_usuario_p);
				end;
			end loop;--C01
			-- seta os registros que serão válidos ou inválidos após o processamento
			CALL pls_tipos_ocor_pck.atualiza_campo_valido('V', nr_id_transacao_p, null, dados_regra_p);
		end if;
	end loop;--C03
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_oc_cta_tratar_val_14 ( dados_regra_p pls_tipos_ocor_pck.dados_regra, nr_id_transacao_p pls_selecao_ocor_cta.nr_id_transacao%type, nm_usuario_p usuario.nm_usuario%type) FROM PUBLIC;

