SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_oc_cta_tratar_val_22_imp ( nr_seq_combinada_p pls_oc_cta_combinada.nr_sequencia%type, ie_regra_excecao_p pls_oc_cta_combinada.ie_excecao%type, nr_id_transacao_p pls_oc_cta_selecao_imp.nr_id_transacao%type) AS $body$
DECLARE


_ora2pg_r RECORD;
nr_index_sel_w				integer;
ie_registro_valido_w			varchar(1);
tb_seq_selecao_w			pls_util_cta_pck.t_number_table;
tb_valido_w				pls_util_cta_pck.t_varchar2_table_1;
tb_observacao_w				pls_util_cta_pck.t_varchar2_table_4000;

-- Informações da regra de validação da senha X autorização.
C01 CURSOR(	nr_seq_oc_cta_comb_pc	pls_oc_cta_combinada.nr_sequencia%type) FOR
	SELECT	nr_sequencia,
		ie_valida_glosa_1406
	from	pls_oc_cta_val_glo1406
	where	nr_seq_oc_cta_comb	= nr_seq_oc_cta_comb_pc;

--Cursor com a contas selecionadas no qual serão geradas as ocorrências
C02 CURSOR(	nr_id_transacao_pc		pls_oc_cta_selecao_imp.nr_id_transacao%type) FOR
	SELECT	conta.nr_sequencia		nr_seq_conta,
		coalesce(conta.cd_senha,'0')		cd_senha_conta,
		conta.nr_seq_segurado_conv	nr_seq_segurado_conv,
		conta.cd_guia_ok_conv		cd_guia_ok_conv
	from	pls_conta_imp	conta
	where	exists (	SELECT 1
				from	pls_oc_cta_selecao_imp	sel
				where	sel.nr_id_transacao 	= nr_id_transacao_pc
				and  	sel.ie_valido 		= 'S'
				and  	sel.nr_seq_conta 	= conta.nr_sequencia);

--Cursor com as guias do plano vinculadas ao segurado e guia
C03 CURSOR(	nr_seq_seg_conv_pc	pls_conta_imp.nr_seq_segurado_conv%type,
		cd_gui_conv_pc		pls_conta_imp.cd_guia_ok_conv%type) FOR
	SELECT	coalesce(guia.cd_senha,'0')	cd_senha_guia
	from	pls_guia_plano		guia
	where	guia.nr_seq_segurado	= nr_seq_seg_conv_pc
	and	guia.ie_status		= '1'
	and	guia.cd_guia		= cd_gui_conv_pc;
BEGIN

if (nr_seq_combinada_p IS NOT NULL AND nr_seq_combinada_p::text <> '') and (nr_id_transacao_p IS NOT NULL AND nr_id_transacao_p::text <> '')  then
	-- Informações da regra de validação da senha externa X autorização.
	for	r_C01_w in C01(nr_seq_combinada_p) loop
		-- Só aplica a validação caso seja informado na regra que é para fazê-lo
		if (r_C01_w.ie_valida_glosa_1406 = 'S') then

			-- tratamento em campo auxiliar para identificar posteriormente os registros que foram alterados
			CALL pls_ocor_imp_pck.atualiza_campo_auxiliar('V', 'N', nr_id_transacao_p, null);
			--limpa as variáveis
			SELECT * FROM pls_ocor_imp_pck.limpar_nested_tables(	tb_seq_selecao_w, tb_valido_w, tb_observacao_w) INTO STRICT _ora2pg_r;
 	tb_seq_selecao_w := _ora2pg_r.tb_nr_seq_selecao_p; tb_valido_w := _ora2pg_r.tb_ie_valido_p; tb_observacao_w := _ora2pg_r.tb_ds_observacao_p;
			nr_index_sel_w := 0;
			--Abre o cursor das contas
			for	r_C02_w in C02(nr_id_transacao_p) loop

				ie_registro_valido_w := 'N';

				for	r_C03_w in C03(r_C02_w.nr_seq_segurado_conv, r_C02_w.cd_guia_ok_conv) loop

					-- Se for para verificar a senha interna então procura pela autorização olhando pela senha informada na conta.
					if (r_C03_w.cd_senha_guia != r_C02_w.cd_senha_conta) then
						ie_registro_valido_w := 'S';
						exit;
					end if;

				end loop; --C03
				if (ie_registro_valido_w = 'S') then

					tb_seq_selecao_w(nr_index_sel_w) := r_C02_w.nr_seq_conta;
					tb_observacao_w(nr_index_sel_w)  := null;
					tb_valido_w(nr_index_sel_w) 	 := 'S';

					if (nr_index_sel_w >= pls_cta_consistir_pck.qt_registro_transacao_w ) then

						--Grava o que restar nas variáveis na tabela
						CALL pls_ocor_imp_pck.gerencia_selecao_validacao(	tb_seq_selecao_w, tb_valido_w,
												tb_observacao_w, nr_id_transacao_p,
												'SEQ_CONTA');
						--limpa as variáveis
						SELECT * FROM pls_ocor_imp_pck.limpar_nested_tables(	tb_seq_selecao_w, tb_valido_w, tb_observacao_w) INTO STRICT _ora2pg_r;
 	tb_seq_selecao_w := _ora2pg_r.tb_nr_seq_selecao_p; tb_valido_w := _ora2pg_r.tb_ie_valido_p; tb_observacao_w := _ora2pg_r.tb_ds_observacao_p;

						nr_index_sel_w		:= 0;
					else
						nr_index_sel_w := nr_index_sel_w + 1;
					end if;
				end if;
			end loop; --C02
		end if;
	end loop; -- C01
	--Grava o que restar nas variáveis na tabela
	CALL pls_ocor_imp_pck.gerencia_selecao_validacao(	tb_seq_selecao_w, tb_valido_w,
							tb_observacao_w, nr_id_transacao_p,
							'SEQ_CONTA');
	-- seta os registros que serão válidos ou inválidos após o processamento
	CALL pls_ocor_imp_pck.atualiza_campo_valido('V', 'N',
						ie_regra_excecao_p, null,
						nr_id_transacao_p, null);
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_oc_cta_tratar_val_22_imp ( nr_seq_combinada_p pls_oc_cta_combinada.nr_sequencia%type, ie_regra_excecao_p pls_oc_cta_combinada.ie_excecao%type, nr_id_transacao_p pls_oc_cta_selecao_imp.nr_id_transacao%type) FROM PUBLIC;

