SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_oc_cta_tratar_val_26 ( dados_regra_p pls_tipos_ocor_pck.dados_regra, nr_id_transacao_p pls_oc_cta_selecao_ocor_v.nr_id_transacao%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	Aplicar a validação para a ocorrência de prazo para cobrança da taxa adiministrativa. Será
	gerada a ocorrência para as contas que tiverem sido apresentadas após o prazo definido no cadastro
	da operadora congênere na função OPS - Cooperativas Médicas.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[ X]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
------------------------------------------------------------------------------------------------------------------
Pontos de atenção:

Alterações:
------------------------------------------------------------------------------------------------------------------
jjung OS 606930 - 19/08/2013 - Criação da rotina.
------------------------------------------------------------------------------------------------------------------
jjung OS 606930 - 14/10/2013 -

Alteração:	Movido o campo ie_tipo_data da tabela PLS_OC_CTA_VAL_TX_ADM para a tabela
	PLS_REGRA_INTERCAMBIO.

Motivo:	Foi identificado que além da geração da ocorrência o cálculo da taxa também deveria ser feito em cima
	deste campo.
------------------------------------------------------------------------------------------------------------------
dlehmkuhl OS 688483 - 14/04/2014 -

Alteração:	Modificada a forma de trabalho em relação a atualização dos campos de controle
	que basicamente decidem se a ocorrência será ou não gerada. Foi feita também a
	substituição da rotina obterX_seX_geraX.

Motivo:	Necessário realizar essas alterações para corrigir bugs principalmente no que se
	refere a questão de aplicação de filtros (passo anterior ao da validação). Também
	tivemos um foco especial em performance, visto que a mesma precisou ser melhorada
	para não inviabilizar a nova solicitação que diz que a exceção deve verificar todo
	o atendimento.
------------------------------------------------------------------------------------------------------------------
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
dados_tb_selecao_w	pls_tipos_ocor_pck.dados_table_selecao_ocor;

-- Informações da validação de período de internação com relação ao item
c_regra_validacao CURSOR(	nr_seq_oc_cta_comb_p	dados_regra_p.nr_sequencia%type) FOR
	SELECT	a.nr_sequencia	nr_seq_validacao,
		a.ie_valida_limitacao
	from	pls_oc_cta_val_tx_adm a
	where	a.nr_seq_oc_cta_comb	= nr_seq_oc_cta_comb_p;

cs_itens CURSOR(	nr_id_transacao_pc	pls_oc_cta_selecao_ocor_v.nr_id_transacao%type) FOR
	SELECT	sel.nr_sequencia nr_seq_selecao,
		'A cobrança foi enviada fora do prazo de cobrança da taxa de intercâmbio ou não foi definida ' ||
		'uma regra de intercâmbio para este item, porém foi identificado uma cobrança de taxa de intercâmbio nos valores: ' || pls_tipos_ocor_pck.enter_w ||
		'Taxa apresentada: ' || proc.tx_intercambio_imp || pls_tipos_ocor_pck.enter_w ||
		'Taxa calculada: ' || proc.tx_intercambio ds_observacao,
		'S' ie_valido
	from	pls_oc_cta_selecao_ocor_v	sel,
		pls_conta_proc_ocor_v		proc
	where	sel.nr_id_transacao = nr_id_transacao_pc
	and	sel.ie_valido = 'S'
	and	proc.nr_sequencia = sel.nr_seq_conta_proc
	and	coalesce(proc.nr_seq_regra_tx_inter::text, '') = ''
	and (proc.tx_intercambio <> 0 or proc.tx_intercambio_imp <> 0)
	and	proc.ie_origem_conta	= 'A'
	
union all

	SELECT	sel.nr_sequencia nr_seq_selecao,
		'A cobrança foi enviada fora do prazo de cobrança da taxa de intercâmbio ou não foi definida ' ||
		'uma regra de intercâmbio para este item, porém foi identificado uma cobrança de taxa de intercâmbio nos valores: ' || pls_tipos_ocor_pck.enter_w ||
		'Taxa apresentada: ' || mat.tx_intercambio_imp || pls_tipos_ocor_pck.enter_w ||
		'Taxa calculada: ' || mat.tx_intercambio ds_observacao,
		'S' ie_valido
	from	pls_oc_cta_selecao_ocor_v	sel,
		pls_conta_mat_ocor_v		mat
	where	sel.nr_id_transacao = nr_id_transacao_pc
	and	sel.ie_valido = 'S'
	and	mat.nr_sequencia = sel.nr_seq_conta_mat
	and	coalesce(mat.nr_seq_regra_tx_inter::text, '') = ''
	and (mat.tx_intercambio <> 0 or mat.tx_intercambio_imp <> 0)
	and	mat.ie_origem_conta	= 'A';
BEGIN

-- Deve existir informação da regra para aplicar a validação
if (dados_regra_p.nr_sequencia IS NOT NULL AND dados_regra_p.nr_sequencia::text <> '') then

	-- Varrer as regras de validação cadastradas para a regra de ocorrência.
	for	r_c_regra_validacao_w in c_regra_validacao(dados_regra_p.nr_sequencia) loop

		-- Só irá gerar a ocorrência quando a regra estiver marcada para gerar.
		if (r_c_regra_validacao_w.ie_valida_limitacao = 'S') then

			-- tratamento em campo auxiliar para identificar posteriormente os registros que foram alterados
			CALL pls_tipos_ocor_pck.atualiza_campo_auxiliar('V', nr_id_transacao_p, null, dados_regra_p);

			-- Buscar as regbras de intercâmbio cadastradas na função OPS - Cooperativas Médicas \ Regras Gerais \ Taxa/Dias Limite.
			open cs_itens(nr_id_transacao_p);
			loop
				dados_tb_selecao_w.nr_seq_selecao.delete;
				dados_tb_selecao_w.ds_observacao.delete;
				dados_tb_selecao_w.ie_valido.delete;

				fetch cs_itens bulk collect
				into	dados_tb_selecao_w.nr_seq_selecao, dados_tb_selecao_w.ds_observacao,
					dados_tb_selecao_w.ie_valido
				limit	pls_util_cta_pck.qt_registro_transacao_w;

				exit when dados_tb_selecao_w.nr_seq_selecao.count = 0;

				-- Manda as listas para o BD.
				CALL pls_tipos_ocor_pck.gerencia_selecao_validacao(dados_tb_selecao_w.nr_seq_selecao,
					pls_util_cta_pck.clob_table_vazia_w, 'SEQ', dados_tb_selecao_w.ds_observacao,
					dados_tb_selecao_w.ie_valido, nm_usuario_p);

			end loop;
			close cs_itens;
		end if;

		-- seta os registros que serão válidos ou inválidos após o processamento
		CALL pls_tipos_ocor_pck.atualiza_campo_valido('V', nr_id_transacao_p, null, dados_regra_p);

	end loop; -- C02
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_oc_cta_tratar_val_26 ( dados_regra_p pls_tipos_ocor_pck.dados_regra, nr_id_transacao_p pls_oc_cta_selecao_ocor_v.nr_id_transacao%type, nm_usuario_p usuario.nm_usuario%type) FROM PUBLIC;

