SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_oc_cta_tratar_val_30 ( dados_regra_p pls_tipos_ocor_pck.dados_regra, nr_id_transacao_p pls_oc_cta_selecao_ocor_v.nr_id_transacao%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:  Aplicar a validação de materiais não habilitados para o prestador. Esta validação irá verificar
	o cadastro de materiais do prestador, função OPS - Prestador \ pasta Prestadores \ pasta
	Serviços e Materiais \ pasta Materiais.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[ X]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
------------------------------------------------------------------------------------------------------------------
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
dados_table_selecao_ocor_w	pls_tipos_ocor_pck.dados_table_selecao_ocor;
ie_gera_ocorrencia_w		varchar(1);
nr_count_w			integer;

-- Mateirias da tabela de seleçãoq ue não estão habilitados para o prestador quandoa  validação estiver sendo feita na importação do arquivo
cs_mat_n_hab_imp CURSOR(	nr_id_transacao_pc	pls_oc_cta_selecao_ocor_v.nr_id_transacao%type) FOR
	SELECT	sel.nr_sequencia nr_seq_selecao,
		(SELECT	count(1)
		from	table(pls_grupos_aux_pck.obter_materiais_lib_prest(	mat.nr_seq_prestador_exec_imp,
										mat.nr_seq_prest_fornec_imp,
										mat.dt_atendimento_imp,
										mat.ie_tipo_internado,
										prest.nr_seq_tipo_prestador,
										mat.nr_seq_material)) pm) qt_mat_lib,
		mat.nr_seq_prestador_exec_imp nr_seq_prestador_exec_imp,
		mat.nr_seq_prest_fornec_imp nr_seq_prest_fornec_imp
	from	pls_oc_cta_selecao_ocor_v	sel,
		pls_conta_mat_ocor_v		mat,
		pls_prestador			prest
	where	sel.nr_id_transacao		= nr_id_transacao_pc
	and	sel.ie_valido			= 'S'
	and	mat.nr_sequencia		= sel.nr_seq_conta_mat
	and	(mat.nr_seq_material IS NOT NULL AND mat.nr_seq_material::text <> '')
	and	prest.nr_sequencia		= mat.nr_seq_prestador_exec_imp;

-- Materiais da tabela de seleção que não estão habilitados para o prestador quando a validação for feita no TASY.
cs_mat_n_hab CURSOR(	nr_id_transacao_pc	pls_oc_cta_selecao_ocor_v.nr_id_transacao%type) FOR
	SELECT	sel.nr_sequencia nr_seq_selecao,
		(SELECT	count(1)
		from	table(pls_grupos_aux_pck.obter_materiais_lib_prest(	mat.nr_seq_prestador_exec,
										mat.nr_seq_prest_fornec,
										mat.dt_atendimento,
										mat.ie_tipo_internado,
										prest.nr_seq_tipo_prestador,
										mat.nr_seq_material)) pm) qt_mat_lib,
		mat.nr_seq_prestador_exec nr_seq_prestador_exec,
		mat.nr_seq_prest_fornec nr_seq_prest_fornec
	from	pls_oc_cta_selecao_ocor_v	sel,
		pls_conta_mat_ocor_v		mat,
		pls_prestador			prest
	where	sel.nr_id_transacao		= nr_id_transacao_pc
	and	sel.ie_valido			= 'S'
	and	mat.nr_sequencia		= sel.nr_seq_conta_mat
	and	(mat.nr_seq_material IS NOT NULL AND mat.nr_seq_material::text <> '')
	and	prest.nr_sequencia		= mat.nr_seq_prestador_exec;

-- Informações da regra de validação dos materiais não habilitados.
C02 CURSOR(	nr_seq_oc_cta_comb_p	dados_regra_p.nr_sequencia%type) FOR
	SELECT	a.nr_sequencia	nr_seq_validacao,
		a.ie_valida_mat_prestador
	from	pls_oc_cta_val_mat_prest a
	where	a.nr_seq_oc_cta_comb	= nr_seq_oc_cta_comb_p;
BEGIN

-- Só irá executar qualquer copnsulta e\ou validação quando existir informação válida da regra.
if (dados_regra_p.nr_sequencia IS NOT NULL AND dados_regra_p.nr_sequencia::text <> '') then

	-- usa uma tabela para armazenar as estruturas dos materiais
	CALL pls_gerencia_upd_obj_pck.atualizar_objetos(nm_usuario_p, 'PLS_OC_CTA_TRATAR_VAL_30', 'PLS_ESTRUTURA_MATERIAL_TM');

	-- tratamento em campo auxiliar para identificar posteriormente os registros que foram alterados
	CALL pls_tipos_ocor_pck.atualiza_campo_auxiliar('V', nr_id_transacao_p, null, dados_regra_p);

	-- Informações da regra de validação da carteira.
	for	r_C02_w in C02(dados_regra_p.nr_sequencia) loop

		-- Se tiver informação na tabela e a informação for diferente de nenhuma será verificado, caso contrário sai da rotina e nem abre as informações das contas.
		if (r_C02_w.ie_valida_mat_prestador = 'S') then

			nr_count_w := 0;
			-- Quando a consistência for executada durante a importação do arquivo então o acesso as tabela muda e estamos  usando outro cursor.
			-- Favor quando alterar qualquer um dos dois cursores verificar se não deve ser alterado o outro também.
			if (dados_regra_p.ie_evento = 'IMP') then

				-- Buscar os materiais que não estão habilitados para o prestador da conta.
				for 	r_cs_mat_n_hab_imp_w in cs_mat_n_hab_imp( nr_id_transacao_p) loop

					ie_gera_ocorrencia_w := 'N';

					if ( r_cs_mat_n_hab_imp_w.qt_mat_lib = 0) then
						ie_gera_ocorrencia_w := 'S';
					end if;

					if ( ie_gera_ocorrencia_w = 'S') then

						dados_table_selecao_ocor_w.nr_seq_selecao(nr_count_w) := r_cs_mat_n_hab_imp_w.nr_seq_selecao;
						dados_table_selecao_ocor_w.ie_valido(nr_count_w) := 'S';
						dados_table_selecao_ocor_w.ds_observacao(nr_count_w) := pls_oc_cta_obs_val_30(	r_cs_mat_n_hab_imp_w.nr_seq_prestador_exec_imp,	pls_obter_dados_prestador(r_cs_mat_n_hab_imp_w.nr_seq_prestador_exec_imp, 'N'),
															r_cs_mat_n_hab_imp_w.nr_seq_prest_fornec_imp, pls_obter_dados_prestador(r_cs_mat_n_hab_imp_w.nr_seq_prest_fornec_imp, 'N'));

						if ( nr_count_w >= pls_util_pck.qt_registro_transacao_w) then

							CALL pls_tipos_ocor_pck.gerencia_selecao_validacao(	dados_table_selecao_ocor_w.nr_seq_selecao, pls_tipos_ocor_pck.clob_table_vazia,
													'SEQ', dados_table_selecao_ocor_w.ds_observacao,
													dados_table_selecao_ocor_w.ie_valido, nm_usuario_p);
							pls_tipos_ocor_pck.limpar_nested_tables(dados_table_selecao_ocor_w);
							nr_count_w := 0;
						else
							nr_count_w := nr_count_w + 1;
						end if;
					end if;
				end loop; -- cs_mat_n_hab_imp
			else

				-- Buscar os materiais que não estão habilitados para o prestador da conta.
				for 	r_cs_mat_n_hab_w in cs_mat_n_hab( nr_id_transacao_p) loop

					ie_gera_ocorrencia_w := 'N';

					if ( r_cs_mat_n_hab_w.qt_mat_lib = 0) then
						ie_gera_ocorrencia_w := 'S';
					end if;

					if ( ie_gera_ocorrencia_w = 'S') then

						dados_table_selecao_ocor_w.nr_seq_selecao(nr_count_w) := r_cs_mat_n_hab_w.nr_seq_selecao;
						dados_table_selecao_ocor_w.ie_valido(nr_count_w) := 'S';
						dados_table_selecao_ocor_w.ds_observacao(nr_count_w) := pls_oc_cta_obs_val_30(	r_cs_mat_n_hab_w.nr_seq_prestador_exec,	pls_obter_dados_prestador(r_cs_mat_n_hab_w.nr_seq_prestador_exec, 'N'),
															r_cs_mat_n_hab_w.nr_seq_prest_fornec, pls_obter_dados_prestador(r_cs_mat_n_hab_w.nr_seq_prest_fornec, 'N'));

						if ( nr_count_w >= pls_util_pck.qt_registro_transacao_w) then

							CALL pls_tipos_ocor_pck.gerencia_selecao_validacao(	dados_table_selecao_ocor_w.nr_seq_selecao, pls_tipos_ocor_pck.clob_table_vazia,
													'SEQ', dados_table_selecao_ocor_w.ds_observacao,
													dados_table_selecao_ocor_w.ie_valido, nm_usuario_p);
							pls_tipos_ocor_pck.limpar_nested_tables(dados_table_selecao_ocor_w);
							nr_count_w := 0;
						else
							nr_count_w := nr_count_w + 1;
						end if;
					end if;
				end loop; -- cs_mat_n_hab
			end if;
		end if;
	end loop; -- C02
	CALL pls_tipos_ocor_pck.gerencia_selecao_validacao(	dados_table_selecao_ocor_w.nr_seq_selecao, pls_tipos_ocor_pck.clob_table_vazia,
							'SEQ', dados_table_selecao_ocor_w.ds_observacao,
							dados_table_selecao_ocor_w.ie_valido, nm_usuario_p);
	-- seta os registros que serão válidos ou inválidos após o processamento
	CALL pls_tipos_ocor_pck.atualiza_campo_valido('V', nr_id_transacao_p, null, dados_regra_p);
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_oc_cta_tratar_val_30 ( dados_regra_p pls_tipos_ocor_pck.dados_regra, nr_id_transacao_p pls_oc_cta_selecao_ocor_v.nr_id_transacao%type, nm_usuario_p usuario.nm_usuario%type) FROM PUBLIC;

