CREATE OR REPLACE PROCEDURE pls_oc_cta_tratar_val_55_imp ( nr_seq_combinada_p pls_oc_cta_combinada.nr_sequencia%type, ie_regra_excecao_p pls_oc_cta_combinada.ie_excecao%type, nr_id_transacao_p pls_oc_cta_selecao_imp.nr_id_transacao%type) AS $body$
DECLARE


dados_tb_selecao_w		pls_ocor_imp_pck.dados_table_selecao_ocor;

C01 CURSOR(nr_seq_combinada_pc		pls_oc_cta_combinada.nr_sequencia%type) FOR
	SELECT	a.ie_validar_acomodacao
	from	pls_oc_cta_val_item_conta a
	where	a.nr_seq_oc_cta_comb	= nr_seq_combinada_pc;

C02 CURSOR(	nr_id_transacao_pc	pls_selecao_ocor_cta.nr_id_transacao%type,
		ie_validacao_pc		pls_oc_cta_val_item_conta.ie_validar_acomodacao%type) FOR
	SELECT	a.nr_sequencia,
		a.ie_valido,
		null ds_observacao
	from	pls_oc_cta_selecao_imp a
	where	a.nr_id_transacao = nr_id_transacao_pc
	and	a.ie_valido = 'S'
	and	a.ie_tipo_registro = 'P'
	and	exists (	SELECT	1
			from	pls_lib_item_regra_grupo	x,
				pls_lib_item_regra		y,
				pls_conta_proc_regra_lib	z
			where	x.ie_tipo_validacao	= ie_validacao_pc
			and	x.ie_situacao		= 'A'
			and	y.nr_seq_grupo_regra	= x.nr_sequencia
			and	z.nr_seq_regra_carac	= y.nr_sequencia
			and	z.nr_seq_regra_lib	= x.nr_sequencia
			and	z.nr_seq_conta_proc	= a.nr_seq_conta_proc
			and	z.ie_liberado		= 'N');

BEGIN

if (nr_seq_combinada_p IS NOT NULL AND nr_seq_combinada_p::text <> '') then

	--Percorre a lista da seleção, criando select conforme restrições definidas na regra
	for	r_C01_w in C01(nr_seq_combinada_p) loop

		-- tratamento em campo auxiliar para identificar posteriormente os registros que foram alterados
		CALL pls_ocor_imp_pck.atualiza_campo_auxiliar('V', ie_regra_excecao_p, nr_id_transacao_p, null);

		Open C02(nr_id_transacao_p, r_C01_w.ie_validar_acomodacao);
		loop
			fetch C02 bulk collect into	dados_tb_selecao_w.nr_seq_selecao,
							dados_tb_selecao_w.ie_valido,
							dados_tb_selecao_w.ds_observacao
			limit pls_util_cta_pck.qt_registro_transacao_w;

			exit when dados_tb_selecao_w.nr_seq_selecao.count = 0;

			CALL pls_ocor_imp_pck.gerencia_selecao_validacao(	dados_tb_selecao_w.nr_seq_selecao,
									dados_tb_selecao_w.ie_valido,
									dados_tb_selecao_w.ds_observacao,
									nr_id_transacao_p,
									'SEQ');
		end loop;
	end loop;
	-- seta os registros que serão válidos ou inválidos após o processamento
	CALL pls_ocor_imp_pck.atualiza_campo_valido('V', 'N', ie_regra_excecao_p, null, nr_id_transacao_p, null);
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
