CREATE OR REPLACE PROCEDURE pls_oc_cta_tratar_val_61_imp ( nr_seq_combinada_p pls_oc_cta_combinada.nr_sequencia%type, ie_regra_excecao_p pls_oc_cta_combinada.ie_excecao%type, nr_id_transacao_p pls_oc_cta_selecao_imp.nr_id_transacao%type) AS $body$
DECLARE


_ora2pg_r RECORD;
tb_seq_selecao_w	pls_util_cta_pck.t_number_table;
tb_valido_w		pls_util_cta_pck.t_varchar2_table_1;
tb_observacao_w		pls_util_cta_pck.t_varchar2_table_4000;
ds_observacao_1_w	varchar(255);
ds_observacao_2_w	varchar(255);
ds_observacao_3_w	varchar(255);

cs_regras_val CURSOR(nr_seq_oc_cta_comb_pc	pls_oc_cta_combinada.nr_sequencia%type) FOR
	SELECT	a.ie_valida_prest_solic_req
	from	pls_oc_cta_val_pre_sol_req a
	where	a.nr_seq_oc_cta_comb = nr_seq_oc_cta_comb_pc;

cs_gerar_ocor_imp CURSOR(	nr_id_transacao_pc	pls_oc_cta_selecao_imp.nr_id_transacao%type) FOR
	SELECT	sub.nr_sequencia,
		sub.ie_valido,
		sub.ds_observacao
	from (	SELECT	distinct
				conta.nr_sequencia nr_seq_conta,
				ds_observacao_1_w || pls_tipos_ocor_pck.enter_w ||
				ds_observacao_2_w || pls_obter_dados_prestador(conta.nr_seq_prestador_req,'N') || pls_tipos_ocor_pck.enter_w ||
				ds_observacao_3_w || pls_obter_dados_prestador(conta.nr_seq_prest_solic_conv, 'N') ds_observacao,
				sel.nr_sequencia,
				sel.ie_valido
			from	pls_oc_cta_selecao_imp	sel,
				pls_conta_imp_v		conta
			where	sel.nr_id_transacao		= nr_id_transacao_pc
			and	sel.ie_valido			= 'S'
			and	conta.nr_sequencia		= sel.nr_seq_conta
			and	(conta.nr_seq_prest_solic_conv IS NOT NULL AND conta.nr_seq_prest_solic_conv::text <> '')
			and	(conta.nr_seq_prestador_req IS NOT NULL AND conta.nr_seq_prestador_req::text <> '')
			and	conta.nr_seq_prestador_req	<> conta.nr_seq_prest_solic_conv
	) sub;

BEGIN

-- Só processa algo com informação válida
if (nr_seq_combinada_p IS NOT NULL AND nr_seq_combinada_p::text <> '') then

	--O prestador solicitante informado não é o prestador que fez a requisição.
	ds_observacao_1_w:= wheb_mensagem_pck.get_texto(791024);

	ds_observacao_2_w:= wheb_mensagem_pck.get_texto(791045) || ': ';

	ds_observacao_3_w:= wheb_mensagem_pck.get_texto(791044) || ': ';

	-- Informações da regra de validação.
	for	rw_regras_val_w in cs_regras_val(nr_seq_combinada_p) loop

		if (rw_regras_val_w.ie_valida_prest_solic_req = 'S') then

			-- tratamento em campo auxiliar para identificar posteriormente os registros que foram alterados
			CALL pls_ocor_imp_pck.atualiza_campo_auxiliar(	'V',
									'N',
									nr_id_transacao_p,
									null);


			-- Obtém os dados que retornarem do cursor.
			open cs_gerar_ocor_imp(nr_id_transacao_p);
			loop

				-- Preencher as linhas no retorno
				fetch cs_gerar_ocor_imp
				bulk collect into 	tb_seq_selecao_w,
							tb_valido_w,
							tb_observacao_w
				limit pls_util_cta_pck.qt_registro_transacao_w;
				-- abortar quando acabaram as linhas
				exit when tb_seq_selecao_w.count = 0;

				--Grava o que restar nas variáveis na tabela
				CALL pls_ocor_imp_pck.gerencia_selecao_validacao(	tb_seq_selecao_w,
										tb_valido_w,
										tb_observacao_w,
										nr_id_transacao_p,
										'LISTA');
				--limpa as variáveis
				SELECT * FROM pls_ocor_imp_pck.limpar_nested_tables(	tb_seq_selecao_w, tb_valido_w, tb_observacao_w) INTO STRICT _ora2pg_r;
 	tb_seq_selecao_w := _ora2pg_r.tb_nr_seq_selecao_p; tb_valido_w := _ora2pg_r.tb_ie_valido_p; tb_observacao_w := _ora2pg_r.tb_ds_observacao_p;
			end loop;
			close cs_gerar_ocor_imp;


			-- seta os registros que serão válidos ou inválidos após o processamento
			CALL pls_ocor_imp_pck.atualiza_campo_valido('V',
								'N',
								ie_regra_excecao_p,
								null,
								nr_id_transacao_p,
								null);
		end if;
	end loop;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
