CREATE OR REPLACE PROCEDURE pls_oc_cta_tratar_val_66_imp ( nr_seq_combinada_p pls_oc_cta_combinada.nr_sequencia%type, ie_regra_excecao_p pls_oc_cta_combinada.ie_excecao%type, nr_id_transacao_p pls_oc_cta_selecao_imp.nr_id_transacao%type) AS $body$
DECLARE



_ora2pg_r RECORD;
/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Validar período faturamento guia honorário

Será gerada ocorrência caso a data de início de ou fim faturamento da guia de honorário
esteja fora do período inicial e final de faturamento da guia de resumo de internação ao qual ela se refere.

+++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_sql_w		varchar(3000);
ds_restr_w		varchar(1000);

tb_seq_selecao_w	pls_util_cta_pck.t_number_table;
tb_observacao_w		pls_util_cta_pck.t_varchar2_table_4000;
tb_valido_w		pls_util_cta_pck.t_varchar2_table_1;

C01 CURSOR(	nr_seq_oc_cta_comb_p	pls_oc_cta_combinada.nr_sequencia%type) FOR
	SELECT	a.ie_validacao
	from	pls_oc_cta_val_dt_honor a
	where	a.nr_seq_oc_cta_comb	= nr_seq_oc_cta_comb_p;

C02 CURSOR(	nr_id_transacao_pc	pls_oc_cta_selecao_imp.nr_id_transacao%type)FOR
	SELECT 	x.nr_sequencia,
        	'S' ie_valido,
		null ds_observacao
	from	pls_oc_cta_selecao_imp	x,
		pls_conta_imp		a,
		pls_protocolo_conta_imp	b
	where	x.ie_valido		= 'S'
	and	x.nr_id_transacao	= nr_id_transacao_pc
	and	a.nr_sequencia		= x.nr_seq_conta
	and	a.nr_seq_protocolo	= b.nr_sequencia
	and	b.ie_tipo_guia		= '6'
	and 	exists(	SELECT 	c.nr_sequencia
			from	pls_conta_imp c,
				pls_protocolo_conta_imp d
			where	c.cd_guia_ok_conv 	= a.cd_guia_ok_conv
			and	c.nr_seq_protocolo	= d.nr_sequencia
			and	d.ie_tipo_guia		= '5'
			and	c.nr_seq_segurado_conv 	= a.nr_seq_segurado_conv
			and	((a.dt_inicio_faturamento < c.dt_inicio_faturamento) or (a.dt_fim_faturamento 	> c.dt_fim_faturamento))
			
union all

			select 	c.nr_sequencia
			from	pls_conta_v c
			where	c.cd_guia_referencia 	= a.cd_guia_ok_conv
			and	c.nr_seq_segurado 	= a.nr_seq_segurado_conv
			and	c.ie_tipo_guia		= '6'
			and	((a.dt_inicio_faturamento < c.dt_entrada) or (a.dt_fim_faturamento 	> c.dt_alta)));

BEGIN

if (nr_seq_combinada_p IS NOT NULL AND nr_seq_combinada_p::text <> '')  then

	for r_C01_w in C01(nr_seq_combinada_p) loop
		-- tratamento em campo auxiliar para identificar posteriormente os registros que foram alterados
		CALL pls_ocor_imp_pck.atualiza_campo_auxiliar('V', ie_regra_excecao_p, nr_id_transacao_p, null);

		if (r_C01_w.ie_validacao = 'S')	then

			begin
				SELECT * FROM pls_ocor_imp_pck.limpar_nested_tables( tb_seq_selecao_w, tb_valido_w, tb_observacao_w) INTO STRICT _ora2pg_r;
  tb_seq_selecao_w := _ora2pg_r.tb_nr_seq_selecao_p; tb_valido_w := _ora2pg_r.tb_ie_valido_p; tb_observacao_w := _ora2pg_r.tb_ds_observacao_p;
				open C02(nr_id_transacao_p);
				loop
					fetch 	C02 bulk collect
					into	tb_seq_selecao_w, tb_valido_w, tb_observacao_w
					limit pls_util_cta_pck.qt_registro_transacao_w;
					exit when tb_seq_selecao_w.count = 0;

					CALL pls_ocor_imp_pck.gerencia_selecao_validacao( tb_seq_selecao_w, tb_valido_w, tb_observacao_w,
													nr_id_transacao_p,'SEQ');

				end loop;
				close C02;

			exception
			when others then
				CALL pls_ocor_imp_pck.trata_erro_sql_dinamico(nr_seq_combinada_p, null, sqlerrm, nr_id_transacao_p, wheb_usuario_pck.get_nm_usuario, 'N');
			end;
		end if;
	end loop;
	-- seta os registros que serão válidos ou inválidos após o processamento
	CALL pls_ocor_imp_pck.atualiza_campo_valido('V', 'N', ie_regra_excecao_p, null, nr_id_transacao_p, null);

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
