SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_oc_cta_tratar_val_66 ( dados_regra_p pls_tipos_ocor_pck.dados_regra, nr_id_transacao_p pls_selecao_ocor_cta.nr_id_transacao%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE



/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Validar período faturamento guia honorário

Será gerada ocorrência caso a data de início de ou fim faturamento da guia de honorário
esteja fora do período inicial e final de faturamento da guia de resumo de internação ao qual ela se refere.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[X]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
+++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
bind_sql_valor_w		sql_pck.t_dado_bind;
cursor_w			sql_pck.t_cursor;
ds_sql_w			varchar(3000);
ds_restr_w			varchar(1000);

nr_seq_selecao_w	dbms_sql.number_table;
ds_observacao_w		dbms_sql.varchar2_table;
ie_valido_w		dbms_sql.varchar2_table;

C01 CURSOR(	nr_seq_oc_cta_comb_p	dados_regra_p.nr_sequencia%type) FOR
	SELECT	a.ie_validacao
	from	pls_oc_cta_val_dt_honor a
	where	a.nr_seq_oc_cta_comb	= nr_seq_oc_cta_comb_p;

BEGIN

if (dados_regra_p.nr_sequencia IS NOT NULL AND dados_regra_p.nr_sequencia::text <> '')  then

	for r_C01_w in C01(dados_regra_p.nr_sequencia) loop

		if (r_C01_w.ie_validacao = 'S')	then

			CALL pls_tipos_ocor_pck.atualiza_campo_auxiliar('V', nr_id_transacao_p, null, dados_regra_p);

			if (dados_regra_p.ie_evento = 'IMP') then
				ds_restr_w	:=	'			and	((a.dt_inicio_faturamento_imp < b.dt_entrada_imp) or'|| pls_tipos_ocor_pck.enter_w ||
						'				(a.dt_fim_faturamento_imp > b.dt_alta_imp))))';
			else
				ds_restr_w	:=	'			and	((a.dt_inicio_faturamento < b.dt_entrada) or'|| pls_tipos_ocor_pck.enter_w ||
			 			'				(a.dt_fim_faturamento > b.dt_alta))))';
			end if;

			ds_sql_w := 	'	select 		x.nr_sequencia, ' 			|| pls_tipos_ocor_pck.enter_w ||
					'			''S'' ie_valido, '		|| pls_tipos_ocor_pck.enter_w ||
					'			null ds_observacao '			|| pls_tipos_ocor_pck.enter_w ||
					'	from	pls_oc_cta_selecao_ocor_v	x, ' 		|| pls_tipos_ocor_pck.enter_w ||
					'		pls_conta_v		a '			|| pls_tipos_ocor_pck.enter_w ||
					'	where	x.ie_valido		= ''S'' '		|| pls_tipos_ocor_pck.enter_w ||
					'	and	x.nr_id_transacao	= :nr_id_transacao_pc '	|| pls_tipos_ocor_pck.enter_w ||
					'	and	a.nr_sequencia		= x.nr_seq_conta '	|| pls_tipos_ocor_pck.enter_w ||
					'	and	a.ie_tipo_guia		= 6 '			|| pls_tipos_ocor_pck.enter_w ||
					'	and 	(exists(	select 	b.nr_sequencia '			|| pls_tipos_ocor_pck.enter_w ||
					'				from	pls_conta_v b '				|| pls_tipos_ocor_pck.enter_w ||
					'				where	b.cd_guia_referencia = a.cd_guia_referencia '	|| pls_tipos_ocor_pck.enter_w ||
					'				and	b.nr_seq_segurado = a.nr_seq_segurado ';

			ds_sql_w := ds_sql_w || ds_restr_w;

			bind_sql_valor_w := sql_pck.bind_variable(':nr_id_transacao_pc', nr_id_transacao_p, bind_sql_valor_w);

			begin
				bind_sql_valor_w := sql_pck.executa_sql_cursor(ds_sql_w, bind_sql_valor_w);
				nr_seq_selecao_w	:= pls_util_cta_pck.num_table_vazia_w;
				ie_valido_w		:= pls_util_cta_pck.vchr2_table_vazia_w;
				ds_observacao_w		:= pls_util_cta_pck.vchr2_table_vazia_w;

				loop
					fetch 	cursor_w bulk collect
					into	nr_seq_selecao_w,ie_valido_w,ds_observacao_w
					limit pls_util_cta_pck.qt_registro_transacao_w;
					exit when nr_seq_selecao_w.count = 0;

					CALL pls_tipos_ocor_pck.gerencia_selecao_validacao(	nr_seq_selecao_w, pls_util_cta_pck.clob_table_vazia_w,
											'SEQ', ds_observacao_w, ie_valido_w, nm_usuario_p);

				end loop;

				close cursor_w;

			exception
			when others then
				CALL pls_tipos_ocor_pck.trata_erro_sql_dinamico(dados_regra_p, ds_sql_w, nr_id_transacao_p, nm_usuario_p);
			end;

		end if;
	end loop;

	CALL pls_tipos_ocor_pck.atualiza_campo_valido('V', nr_id_transacao_p, null, dados_regra_p);

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_oc_cta_tratar_val_66 ( dados_regra_p pls_tipos_ocor_pck.dados_regra, nr_id_transacao_p pls_selecao_ocor_cta.nr_id_transacao%type, nm_usuario_p usuario.nm_usuario%type) FROM PUBLIC;

