CREATE OR REPLACE PROCEDURE pls_oc_cta_tratar_val_73_imp ( nr_seq_combinada_p pls_oc_cta_combinada.nr_sequencia%type, ie_regra_excecao_p pls_oc_cta_combinada.ie_excecao%type, nr_id_transacao_p pls_oc_cta_selecao_imp.nr_id_transacao%type) AS $body$
DECLARE


_ora2pg_r RECORD;
tb_seq_selecao_w	pls_util_cta_pck.t_number_table;
tb_valido_w		pls_util_cta_pck.t_varchar2_table_1;
tb_observacao_w		pls_util_cta_pck.t_varchar2_table_4000;

C01 CURSOR(	nr_seq_oc_cta_comb_pc	pls_oc_cta_combinada.nr_sequencia%type) FOR
	SELECT	a.ie_valida_unidade_medida ie_valida_unidade_medida,
		a.ie_valida_unidade_medida_ptu ie_valida_unidade_medida_ptu
	from	pls_oc_cta_val_uni_med a
	where	a.nr_seq_oc_cta_comb	= nr_seq_oc_cta_comb_pc;

C02 CURSOR(	nr_id_transacao_pc		pls_oc_cta_selecao_imp.nr_sequencia%type) FOR
	SELECT 	x.nr_sequencia,
		'S' ie_valido,
		obter_desc_expressao(772434) ds_observacao
	from	pls_oc_cta_selecao_imp	x,
		pls_conta_mat_imp	a
	where	x.ie_valido		= 'S'
	and	x.nr_id_transacao	= nr_id_transacao_pc
	and	a.nr_sequencia 		= x.nr_seq_conta_mat
	and	(a.cd_unidade_medida IS NOT NULL AND a.cd_unidade_medida::text <> '')
	and	(a.nr_seq_material_conv IS NOT NULL AND a.nr_seq_material_conv::text <> '')
	and	not exists (SELECT 	1
			from 		pls_material		b,
					tiss_unidade_medida	c,
					unidade_medida		d
			where		b.nr_sequencia		= a.nr_seq_material_conv
			and		a.cd_unidade_medida	= c.cd_unidade_medida
			and		c.nr_sequencia		= d.nr_seq_medida_tiss
			and		d.cd_unidade_medida	= b.cd_unidade_medida);


BEGIN
if (nr_seq_combinada_p IS NOT NULL AND nr_seq_combinada_p::text <> '')  then
	for r_C01_w in C01(nr_seq_combinada_p) loop
		if (r_C01_w.ie_valida_unidade_medida = 'S') then
			begin
				-- Incializar as listas para cada regra.
				SELECT * FROM pls_ocor_imp_pck.limpar_nested_tables(	tb_seq_selecao_w, tb_valido_w, tb_observacao_w) INTO STRICT _ora2pg_r;
 	tb_seq_selecao_w := _ora2pg_r.tb_nr_seq_selecao_p; tb_valido_w := _ora2pg_r.tb_ie_valido_p; tb_observacao_w := _ora2pg_r.tb_ds_observacao_p;

				-- tratamento em campo auxiliar para identificar posteriormente os registros que foram alterados
				CALL pls_ocor_imp_pck.atualiza_campo_auxiliar(	'V',
										'N',
										nr_id_transacao_p,
										null);

				open c02(nr_id_transacao_p);
				loop
					fetch 	c02 bulk collect
					into	tb_seq_selecao_w,
						tb_valido_w,
						tb_observacao_w

					limit pls_util_cta_pck.qt_registro_transacao_w;
					exit when tb_seq_selecao_w.count = 0;

					CALL pls_ocor_imp_pck.gerencia_selecao_validacao(	tb_seq_selecao_w,
											tb_valido_w,
											tb_observacao_w,
											nr_id_transacao_p,
											'SEQ');

				end loop;
				close c02;
			exception
			when others then
				if (c02%isopen) then
					close c02;
				end if;
			end;
			CALL pls_ocor_imp_pck.atualiza_campo_valido('V',
								'N',
								ie_regra_excecao_p,
								null,
								nr_id_transacao_p,
								null);
		end if;


	end loop;
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
