CREATE OR REPLACE PROCEDURE pls_oc_cta_tratar_val_7_imp ( nr_seq_combinada_p pls_oc_cta_combinada.nr_sequencia%type, ie_regra_excecao_p pls_oc_cta_combinada.ie_excecao%type, nr_id_transacao_p pls_oc_cta_selecao_imp.nr_id_transacao%type) AS $body$
DECLARE


_ora2pg_r RECORD;
tb_seq_selecao_w	pls_util_cta_pck.t_number_table;
tb_valido_w		pls_util_cta_pck.t_varchar2_table_1;
tb_observacao_w		pls_util_cta_pck.t_varchar2_table_4000;
ds_obs_w		varchar(255);

-- Informações da validação de não-utilização de item autorizado
C01 CURSOR(	nr_seq_oc_cta_comb_p	pls_oc_cta_combinada.nr_sequencia%type) FOR
SELECT	a.ie_aut_utilizacao
from	pls_oc_cta_val_util_autor a
where	a.nr_seq_oc_cta_comb	= nr_seq_oc_cta_comb_p;

-- Este select busca os itens das contas que estão na seleção e foram autorizados porém não foram utilizados.
-- Estes procedimentos podem ser obtidos na pls_item_aut_nao_util_v, o retorno desta view está sendo colocado em uma function que agrupa as linhas e retorna os resultados
-- em uma mesma linha, utilizando um separador que for passado no segundo parâmetro.
C02 CURSOR(	nr_id_transacao_pc	pls_oc_cta_selecao_imp.nr_id_transacao%type,
		ds_obs_pc		text) FOR
	SELECT	x.nr_sequencia,
		'S' ie_valido,
		(ds_obs_pc || pls_tipos_ocor_pck.enter_w || x.ds_itens) ds_observacao
	from (
		SELECT	sel.nr_sequencia,
			pls_admin_cursor.obter_desc_cursor(cursor(select	itens.ds_tipo || ': ' || itens.ds_item
								  from		pls_item_aut_nao_util_v itens
								  where		itens.nr_seq_conta = sel.nr_seq_conta
								  order by	itens.ds_tipo), chr(13) || chr(10)) ds_itens
		from	pls_oc_cta_selecao_imp sel
		where	sel.nr_id_transacao = nr_id_transacao_pc
		and	sel.ie_valido = 'S') x
	where 	(x.ds_itens IS NOT NULL AND x.ds_itens::text <> '');

BEGIN

-- Deve ter a informação da regra para que seja aplicada a validação.
if (nr_seq_combinada_p IS NOT NULL AND nr_seq_combinada_p::text <> '') and (nr_id_transacao_p IS NOT NULL AND nr_id_transacao_p::text <> '') then
	--Texto : Os seguintes itens foram autorizados mas não foram utilizados:
	ds_obs_w := wheb_mensagem_pck.get_texto(372430);

	--Abre cursor das regras de validação
	for	r_C01_w in C01(nr_seq_combinada_p) loop

		-- Somente aplicar a validação se a regra disse que é para aplicar a validação
		if (r_C01_w.ie_aut_utilizacao = 'S') then

			-- tratamento em campo auxiliar para identificar posteriormente os registros que foram alterados
			CALL pls_ocor_imp_pck.atualiza_campo_auxiliar('V', 'N', nr_id_transacao_p, null);
			--limpa as variáveis
			SELECT * FROM pls_ocor_imp_pck.limpar_nested_tables(	tb_seq_selecao_w, tb_valido_w, tb_observacao_w) INTO STRICT _ora2pg_r;
 	tb_seq_selecao_w := _ora2pg_r.tb_nr_seq_selecao_p; tb_valido_w := _ora2pg_r.tb_ie_valido_p; tb_observacao_w := _ora2pg_r.tb_ds_observacao_p;
			begin
				-- Abertura do cursor e execução do comando.
				open C02(nr_id_transacao_p, ds_obs_w);
				loop
					-- Gravar novas linhas
					fetch C02 bulk collect into  tb_seq_selecao_w, tb_valido_w, tb_observacao_w
					limit pls_util_cta_pck.qt_registro_transacao_w;
					-- Se terminou aborta
					exit when tb_seq_selecao_w.count = 0;

					--Grava as informações na tabela de seleção
					CALL pls_ocor_imp_pck.gerencia_selecao_validacao(	tb_seq_selecao_w, tb_valido_w,
											tb_observacao_w, nr_id_transacao_p,
											'SEQ');
					--limpa as variáveis
					SELECT * FROM pls_ocor_imp_pck.limpar_nested_tables(	tb_seq_selecao_w, tb_valido_w, tb_observacao_w) INTO STRICT _ora2pg_r;
 	tb_seq_selecao_w := _ora2pg_r.tb_nr_seq_selecao_p; tb_valido_w := _ora2pg_r.tb_ie_valido_p; tb_observacao_w := _ora2pg_r.tb_ds_observacao_p;
					commit;
				end loop;
				close C02;
			exception
			when others then
				--Fecha cursor
				if (C02%isopen) then
					close C02;
				end if;
			end;
			--Se tiver alguma informaçõa grava na tabela
			CALL pls_ocor_imp_pck.gerencia_selecao_validacao(	tb_seq_selecao_w, tb_valido_w,
									tb_observacao_w, nr_id_transacao_p,
									'SEQ');
			-- seta os registros que serão válidos ou inválidos após o processamento
			CALL pls_ocor_imp_pck.atualiza_campo_valido('V', 'N',
								ie_regra_excecao_p, null,
								nr_id_transacao_p, null);
		end if;
	end loop; -- C01
end if;
end;
$body$
