CREATE OR REPLACE PROCEDURE pls_oc_gerar_grau_espec ( nr_seq_conta_p bigint, nr_seq_proc_p bigint, nr_seq_ocorrencia_p bigint, ie_reconsistencia_p text, nm_usuario_p text, cd_estabelecimento_p bigint, ie_gerou_ocorrencia_p INOUT text, ie_tipo_ocorrencia_p text ) AS $body$
DECLARE


cd_medico_w			bigint;
nr_seq_segurado_w		bigint;
nr_seq_grau_partic_w		bigint;
ie_gerou_ocorrencia_w		varchar(1)	:= 'N';
qt_participante_w		bigint;
nr_Seq_participante_w		bigint;

C01 CURSOR FOR
	SELECT	a.nr_seq_grau_partic,
		a.cd_medico,
		c.nr_seq_segurado,
		a.nr_Sequencia
	from	pls_proc_participante a,
		pls_conta_proc	b,
		pls_conta	c
	where	a.nr_seq_conta_proc	= nr_seq_proc_p
	and	a.nr_seq_conta_proc	= b.nr_sequencia
	and	c.nr_sequencia		= b.nr_seq_conta;


BEGIN
select	count(*)
into STRICT	qt_participante_w
from	pls_proc_participante a,
	pls_conta_proc	b
where	a.nr_seq_conta_proc	= b.nr_sequencia
and	b.nr_sequencia		= nr_seq_proc_p;

if (coalesce(nr_seq_proc_p,0) > 0)	and (qt_participante_w > 0)		then

	open C01;
	loop
	fetch C01 into
		nr_seq_grau_partic_w,
		cd_medico_w,
		nr_seq_segurado_w,
		nr_Seq_participante_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		ie_gerou_ocorrencia_w := pls_consitir_grau_espec(nr_seq_grau_partic_w, cd_medico_w, nr_seq_conta_p, nr_seq_proc_p, cd_estabelecimento_p, nr_seq_segurado_w, nm_usuario_p, ie_gerou_ocorrencia_w, nr_seq_ocorrencia_p, ie_reconsistencia_p, ie_tipo_ocorrencia_p, nr_Seq_participante_w);
		end;
	end loop;
	close C01;
else
	select	nr_seq_grau_partic,
		cd_medico_executor,
		nr_seq_segurado
	into STRICT	nr_seq_grau_partic_w,
		cd_medico_w,
		nr_seq_segurado_w
	from	pls_conta
	where	nr_sequencia = nr_seq_conta_p;
	ie_gerou_ocorrencia_w := pls_consitir_grau_espec(nr_seq_grau_partic_w, cd_medico_w, nr_seq_conta_p, nr_seq_proc_p, cd_estabelecimento_p, nr_seq_segurado_w, nm_usuario_p, ie_gerou_ocorrencia_w, nr_seq_ocorrencia_p, ie_reconsistencia_p, ie_tipo_ocorrencia_p, null);
end if;

ie_gerou_ocorrencia_p := ie_gerou_ocorrencia_w;

end;
$body$
