SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_organizar_grupos_nivel_lib ( nr_seq_auditoria_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_grupo_w			bigint;
nr_maior_nivel_w		bigint;
nr_nivel_grupo_w		bigint;
nr_seq_grupo_auditoria_w	bigint;
qt_grupos_externo_w		integer;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		nr_seq_grupo
	from	pls_auditoria_grupo
	where	nr_seq_auditoria = nr_seq_auditoria_p
	order by nr_sequencia;


BEGIN

select	count(*)
into STRICT	qt_grupos_externo_w
from	pls_auditoria_grupo a,
	pls_grupo_auditor b
where	a.nr_seq_grupo 		= b.nr_sequencia
and	b.ie_tipo_auditoria 	= 2
and	a.nr_seq_auditoria 	= nr_seq_auditoria_p;

if (qt_grupos_externo_w > 0) then
	begin
		select	max(pls_obter_nivel_regra_grup_aud(nr_seq_grupo))
		into STRICT	nr_maior_nivel_w
		from	pls_auditoria_grupo
		where	nr_seq_auditoria = nr_seq_auditoria_p;
	exception
	when others then
		nr_maior_nivel_w := 0;
	end;

	if (coalesce(nr_maior_nivel_w,0) > 0) then
		open C01;
		loop
		fetch C01 into
			nr_seq_grupo_auditoria_w,
			nr_seq_grupo_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			begin

			select	pls_obter_nivel_regra_grup_aud(nr_seq_grupo_w)
			into STRICT	nr_nivel_grupo_w
			;

			if (coalesce(nr_nivel_grupo_w,0) > 0) then
				if (nr_nivel_grupo_w < nr_maior_nivel_w) then
					delete from pls_auditoria_grupo
					where	nr_sequencia = nr_seq_grupo_auditoria_w;
				end if;
			end if;
			end;
		end loop;
		close C01;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
