CREATE OR REPLACE PROCEDURE pls_parecer_glosar_discussao (nr_sequencia_p bigint, nr_seq_motivo_glosa_negada_p bigint, ds_parecer_p text, nr_seq_grupo_p bigint, nm_usuario_p text, cd_estabelecimento_p text) AS $body$
DECLARE


qt_recurso_w		double precision;
vl_recurso_w		double precision;
nr_seq_conta_w		bigint;
nr_seq_analise_w	bigint;
ie_tipo_item_w		varchar(1);
ds_observacao_w		varchar(4000);
ds_motivo_w		varchar(255);

qt_negada_w		double precision;
vl_negado_w		double precision;


BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	select	coalesce(qt_recurso,0),
		coalesce(vl_recurso,0)
	into STRICT	qt_recurso_w,
		vl_recurso_w
	from	w_pls_discussao_item
	where	nr_sequencia = nr_sequencia_p;

	select	max(nr_seq_conta),
		max(nr_seq_analise),
		max(ie_tipo_item)
	into STRICT	nr_seq_conta_w,
		nr_seq_analise_w,
		ie_tipo_item_w
	from	w_pls_discussao_item
	where	nr_sequencia = nr_sequencia_p;

	if (nr_seq_motivo_glosa_negada_p IS NOT NULL AND nr_seq_motivo_glosa_negada_p::text <> '') then
		select	max(ds_motivo)
		into STRICT	ds_motivo_w
		from	pls_motivo_glosa_negada
		where	nr_sequencia = 	nr_seq_motivo_glosa_negada_p;

		insert into pls_analise_parecer_disc	/* Parecer */
			(nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			ds_parecer,
			nr_seq_w_disc_item,
			nr_seq_motivo_aceite,
			nr_seq_motivo_neg)
		values (nextval('pls_analise_parecer_disc_seq'),
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			ds_parecer_p,
			nr_sequencia_p,
			null,
			nr_seq_motivo_glosa_negada_p);

		ds_observacao_w := 	'Tipo de liberação:  '||chr(13)||chr(10)||
					chr(9)||'Liberação desfavorável'||chr(13)||chr(10)||chr(13)||chr(10)||
					'Motivo de liberação:'||chr(13)||chr(10)||
					chr(9)||ds_motivo_w||chr(13)||chr(10)||chr(13)||chr(10)||
					'Observação/Parecer: '||chr(13)||chr(10)||
					chr(9)||ds_parecer_p;

		/* Histórico */

		CALL pls_inserir_hist_analise(nr_seq_conta_w,nr_seq_analise_w,26,nr_sequencia_p,ie_tipo_item_w,null,null,ds_observacao_w,nr_seq_grupo_p,
					nm_usuario_p,cd_estabelecimento_p);

	end if;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
