SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_participacao_fin_contrato ( nr_seq_contrato_p bigint, ie_participacao_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_participacao_ant_w pls_contrato.ie_participacao%type;
ds_participacao_nova_w varchar(255);
ds_participacao_ant_w varchar(255);

BEGIN

select 	ie_participacao
into STRICT	ie_participacao_ant_w
from 	pls_contrato
where 	nr_sequencia = nr_seq_contrato_p;

if (ie_participacao_ant_w <> ' ') then
	select	ds_expressao
	into STRICT	ds_participacao_ant_w
	from	valor_dominio_v
	where	cd_dominio = 2156
	and 	vl_dominio = ie_participacao_ant_w;
end if;

if (ie_participacao_p <> ' ') then

	select	ds_expressao
	into STRICT	ds_participacao_nova_w
	from	valor_dominio_v
	where	cd_dominio = 2156
	and 	vl_dominio = ie_participacao_p;

	update pls_contrato
	set ie_participacao = ie_participacao_p
	where nr_sequencia = nr_seq_contrato_p;

	insert into pls_contrato_historico(	nr_sequencia, dt_atualizacao, nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
			cd_estabelecimento, nr_seq_contrato, dt_historico, ie_tipo_historico, ds_historico,
			ds_observacao)
		values (	nextval('pls_contrato_historico_seq'), clock_timestamp(), nm_usuario_p, clock_timestamp(), nm_usuario_p,
			cd_estabelecimento_p, nr_seq_contrato_p, clock_timestamp(), '98', 'Participação antiga: ' || coalesce(ds_participacao_ant_w,' ')|| ' para a participação: ' ||ds_participacao_nova_w,
			'pls_participacao_fin_contrato');

elsif (coalesce(ie_participacao_p,' ') = ' ') then

	update pls_contrato
	set ie_participacao  = NULL
	where nr_sequencia = nr_seq_contrato_p;

	insert into pls_contrato_historico(	nr_sequencia, dt_atualizacao, nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
			cd_estabelecimento, nr_seq_contrato, dt_historico, ie_tipo_historico, ds_historico,
			ds_observacao)
		values (	nextval('pls_contrato_historico_seq'), clock_timestamp(), nm_usuario_p, clock_timestamp(), nm_usuario_p,
			cd_estabelecimento_p, nr_seq_contrato_p, clock_timestamp(), '98', 'Participação antiga: ' || coalesce(ds_participacao_ant_w,' ')|| ' para a participação: ' ||' ',
			'pls_participacao_fin_contrato');

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
