CREATE OR REPLACE PROCEDURE pls_pf_aprovar_campos_solic ( nr_seq_solicitacao_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_solic_alt_campo_w	bigint;

C01 CURSOR FOR
	SELECT nr_sequencia
	from tasy_solic_alt_campo
	where nr_seq_solicitacao = nr_seq_solicitacao_p
	and ie_status = 'P';


BEGIN

Open C01;
loop
fetch C01 into
	nr_seq_solic_alt_campo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	update	tasy_solic_alt_campo
	set	ie_status = 'A',
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p
	where	nr_sequencia = nr_seq_solic_alt_campo_w;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
