CREATE OR REPLACE PROCEDURE pls_pf_marcar_revisao ( nm_usuario_p text, cd_pessoa_fisica_p text, ie_revisar_p bigint) AS $body$
DECLARE


ie_revisar_w		varchar(10) := 'R';


BEGIN

if (ie_revisar_p	= 1) then
	ie_revisar_w	:= 'R';
elsif (ie_revisar_p	= 2) then
	ie_revisar_w	:= 'N';
end if;

update	pessoa_fisica
set	ie_revisar		= ie_revisar_w,
	dt_atualizacao		= clock_timestamp(),
	nm_usuario		= nm_usuario_p
where	cd_pessoa_fisica	= cd_pessoa_fisica_p;

commit;

end;
