CREATE OR REPLACE PROCEDURE pls_pf_reprovar_campos_solic ( nr_seq_solicitacao_p bigint, ds_observacao_p text, nr_seq_motivo_reprov_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_solic_alt_campo_w	bigint;

C01 CURSOR FOR
	SELECT nr_sequencia
	from tasy_solic_alt_campo
	where nr_seq_solicitacao = nr_seq_solicitacao_p
	and ie_status = 'P';


BEGIN

Open C01;
loop
fetch C01 into
	nr_seq_solic_alt_campo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	update 	tasy_solic_alt_campo
	set	ie_status	= 'R',
		ds_observacao	= ds_observacao_p,
		dt_atualizacao	= clock_timestamp(),
		nr_seq_motivo_rejeicao	= nr_seq_motivo_reprov_p
	where	nr_sequencia = nr_seq_solic_alt_campo_w;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
