SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_portab_gravar_inconsist ( nr_seq_portabilidade_p bigint, cd_inconsistencia_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_inco_w			bigint;
nr_sequencia_w			bigint;


BEGIN

select	max(nr_sequencia)
into STRICT	nr_seq_inco_w
from	pls_portab_inconsistencia
where	cd_inconsistencia	= cd_inconsistencia_p
and	ie_situacao		= 'A';

if (nr_seq_inco_w IS NOT NULL AND nr_seq_inco_w::text <> '') then

	select	coalesce(max(nr_sequencia),0) + 1
	into STRICT	nr_sequencia_w
	from	pls_portab_solic_consist
	where	nr_seq_portabilidade_p	= nr_seq_portabilidade_p;

	insert into pls_portab_solic_consist(nr_seq_portabilidade, nr_sequencia, cd_estabelecimento,
		nr_seq_inconsistencia, dt_atualizacao, nm_usuario,
		dt_atualizacao_nrec, nm_usuario_nrec)
	values (	nr_seq_portabilidade_p, nr_sequencia_w, cd_estabelecimento_p,
		nr_seq_inco_w, clock_timestamp(), nm_usuario_p,
		clock_timestamp(), nm_usuario_p);

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
