CREATE OR REPLACE PROCEDURE pls_portab_liberar_inconsist ( nr_seq_inconsist_p bigint, nm_usuario_p text) AS $body$
BEGIN

update	pls_portab_validacao
set	dt_liberacao	= clock_timestamp(),
	dt_atualizacao	= clock_timestamp(),
	nm_usuario	= nm_usuario_p
where	nr_sequencia	= nr_seq_inconsist_p;

insert into pls_portab_check_list(	nr_sequencia,
					nr_seq_validacao,
					dt_check_list,
					ds_check_list,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec)
				values (	nextval('pls_portab_check_list_seq'),
					nr_seq_inconsist_p,
					clock_timestamp(),
					'Inconsistência liberada pelo usuário: ' || nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p);

commit;

end;
