CREATE OR REPLACE PROCEDURE pls_pp_cta_desfazer_lote_pag ( nr_seq_lote_p pls_pp_lote.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE

					 
ds_stack_w		pls_pp_lote_log_etapa.ds_erro%type;
nr_seq_log_etapa_w	pls_pp_lote_log_etapa.nr_sequencia%type;


BEGIN 
 
begin 
	-- faz o carregamento de parâmetros gerais e armazena em variáveis globais da package pls_pp_lote_pagamento_pck 
	CALL pls_pp_lote_pagamento_pck.carrega_parametros(nr_seq_lote_p, cd_estabelecimento_p);
	 
	-- validação para verificar se a geração do lote pode ser desfeita 
	CALL pls_pp_lote_pagamento_pck.valida_se_pode_des_gerar_lote(nr_seq_lote_p, cd_estabelecimento_p);
	 
	-- limpa todos os registros de geração de um possível lote anterior 
	nr_seq_log_etapa_w := pls_pp_lote_pagamento_pck.grava_tempo_execucao(nr_seq_lote_p, null, 'L', null, nr_seq_log_etapa_w, 'G', nm_usuario_p);
	 
	-- limpa todos os registros de desfazer geração de um possível lote anterior 
	nr_seq_log_etapa_w := pls_pp_lote_pagamento_pck.grava_tempo_execucao(nr_seq_lote_p, null, 'L', null, nr_seq_log_etapa_w, 'D', nm_usuario_p);
	 
	-- limpa todos os registros de geração de título 
	nr_seq_log_etapa_w := pls_pp_lote_pagamento_pck.grava_tempo_execucao(nr_seq_lote_p, null, 'L', null, nr_seq_log_etapa_w, 'T', nm_usuario_p);
	 
	-- exclui todos os eventos de título a receber do lote 
	CALL pls_pp_lote_pagamento_pck.desfazer_item_tit_rec(nr_seq_lote_p, cd_estabelecimento_p, nm_usuario_p);
	 
	-- exclui todos os eventos de título a pagar do lote 
	CALL pls_pp_lote_pagamento_pck.desfazer_item_tit_pag(nr_seq_lote_p, cd_estabelecimento_p, nm_usuario_p);
	 
	-- exclui todos os eventos do lote agrupados por prestador ( pls_pp_prest_event_prest e pls_pp_prestador ) 
	CALL pls_pp_lote_pagamento_pck.desfazer_item_prod_prest(nr_seq_lote_p);
	 
	-- exclui todos os eventos de produção médica do lote agrupados por evento e prestador ( pls_pp_it_prest_event_val e pls_pp_prest_evento_valor ) 
	CALL pls_pp_lote_pagamento_pck.desfazer_item_prod_event_prest(nr_seq_lote_p);
	 
	-- exclui os registros de apropriação e desfaz todas as alterações feitas no lote de pagamento 
	CALL pls_pp_apropriacao_pck.desfazer_aprop_ger_lote_pgto(nr_seq_lote_p);
	 
	-- exclui todos os eventos de lançamentos programados do lote 
	CALL pls_pp_lote_pagamento_pck.desfazer_item_lanc_prog(nr_seq_lote_p, 'GERAR_LOTE');
	 
	-- exclui todos os eventos originados no lote de fechamento 
	CALL pls_pp_lote_retencao_pck.desfazer_valores_retencao_pgto(nr_seq_lote_p);
	 
	-- exclui todos os eventos de adiantamento pago do lote 
	CALL pls_pp_lote_pagamento_pck.desfazer_item_adiant_pag(nr_seq_lote_p, nm_usuario_p);
	 
	-- exclui todos os eventos de desconto de discussao 
	CALL pls_pp_lote_pagamento_pck.desfazer_desc_disc(nr_seq_lote_p);
	 
	-- exclui os itens da pp item que são dos tipo 4 e 5 títulos 
	CALL pls_pp_lote_pagamento_pck.excluir_item_titulo_lote(nr_seq_lote_p);
	 
	-- exclui os itens de recurso de glosa 
	CALL pls_pp_filtro_rec_glosa_pck.desfazer_item_rec_glosa(nr_seq_lote_p);
	 
	-- exclui todos os eventos de produção médica do lote 
	CALL pls_pp_lote_pagamento_pck.desfazer_item_prod_medica(nr_seq_lote_p);
	 
	-- exclui o vínculo da pls_conta_medica_resumo com o lote de pagamento 
	CALL pls_pp_lote_pagamento_pck.desfazer_cta_res_prod_medica(nr_seq_lote_p);
	 
	-- limpa a tabela pls_pp_lote_prest_event, responsável por conter quais prestadores/eventos são permitidos para o lote 
	CALL pls_pp_lote_pagamento_pck.desfazer_lote_prest_event(nr_seq_lote_p);
	 
	-- limpa a data de geração do lote de pagamento 
	CALL pls_pp_lote_pagamento_pck.atualiza_desf_ger_lote_pgto(nr_seq_lote_p, 'S', nm_usuario_p);
	 
exception 
when others then 
	-- limpa os dados de pagamento 
	-- para gerar novamente só desfazendo a geração do anterior 
	CALL pls_pp_lote_pagamento_pck.atualiza_desf_ger_lote_pgto(nr_seq_lote_p, 'N', nm_usuario_p);
	 
	ds_stack_w := dbms_utility.format_call_stack || pls_util_pck.enter_w || 
		   dbms_utility.format_error_backtrace || pls_util_pck.enter_w || 
		   sqlerrm;
	-- grava log se deu erro 
	nr_seq_log_etapa_w := pls_pp_lote_pagamento_pck.grava_tempo_execucao(nr_seq_lote_p, null, 'E', null, nr_seq_log_etapa_w, 'D', nm_usuario_p, ds_stack_w);
end;	
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
