CREATE OR REPLACE PROCEDURE pls_pp_lote_job ( nr_seq_lote_p pls_cta_lote_processo.nr_sequencia%type, ie_opcao_p text, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
DECLARE


/* ie_opcao_p
o mesmo deve ser passado em caixa alta (upper utilizado na rotina pls_criar_pls_pp_lot_job)

GERAR - Ira realizar a geracao do lote de pagamento, 
	buscando valores como producao medica e eventos financeiros
	
FECHAR - Ira fechar o lote de pagamento, gerando tributos e demais 
	valores apos tributacao

TITULO - Ira gerar os titulos a pagar e receber para os prestadores do lote
	alem dos titulos dos tributos que estiverem configurados para tal
 */
nr_seq_log_etapa_w	pls_pp_lote_log_etapa.nr_sequencia%type;


BEGIN
-- verifica se a opcao passada e para gerar o lote de pagamento ou fechar o mesmo
if (ie_opcao_p = 'GERAR') then

	-- gera o lote de pagamento, buscando todos os valores de producao medica, eventos financeiros, titulo, 

	-- adiantamentos, apropriacoes, etc..
	CALL pls_pp_cta_gerar_lote_pag(	nr_seq_lote_p, cd_estabelecimento_p, nm_usuario_p);

elsif (ie_opcao_p = 'FECHAR') then

	-- faz o fechamento do lote realizando o calculo do tributo, buscando valores de eventos financeiros 

	-- sobre valor liquido, gera apropriacoes, etc...
	CALL pls_pp_cta_fechar_lote_pag(	nr_seq_lote_p, cd_estabelecimento_p, nm_usuario_p);

elsif (ie_opcao_p = 'TITULO') then
	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_seq_log_etapa_w
	from	pls_pp_lote_log_etapa
	where	ie_tipo_processo	= 'T' -- Geracao titulo
	and	nr_seq_lote 		= nr_seq_lote_p;
	
	if (nr_seq_log_etapa_w = 0) then
		-- gerar os titulos a pagar e receber para os prestadores do lote

		-- alem dos titulos dos tributos que estiverem configurados para tal
		CALL pls_pp_cta_gerar_tit_lote_pag(	nr_seq_lote_p, cd_estabelecimento_p, nm_usuario_p);
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_pp_lote_job ( nr_seq_lote_p pls_cta_lote_processo.nr_sequencia%type, ie_opcao_p text, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) FROM PUBLIC;

