CREATE OR REPLACE PROCEDURE pls_processa_resp_notif_neg ( nr_seq_notificacao_p bigint, ds_historico_notif_p text, ie_status_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_requisicao_w		bigint;
nr_seq_notificacao_w		bigint;
ie_status_w			varchar(3);
qt_atend_evento_w		bigint;
cd_estabelecimento_w		estabelecimento.cd_estabelecimento%type;


BEGIN
	select 	max(nr_seq_requisicao)
	into STRICT	nr_seq_requisicao_w
	from	pls_notificacao_atend_neg
	where	nr_sequencia	= nr_seq_notificacao_p;

	update	pls_notificacao_atend_neg
	set	ie_status 	= ie_status_p,
		dt_atualizacao	= clock_timestamp(),
		dt_fim_solicitacao = clock_timestamp(),
		nm_usuario	= nm_usuario_p
	where	nr_sequencia	= nr_seq_notificacao_p;

	cd_estabelecimento_w := wheb_usuario_pck.get_cd_estabelecimento;

if (nr_seq_requisicao_w IS NOT NULL AND nr_seq_requisicao_w::text <> '') then
	insert	into pls_requisicao_historico(nr_sequencia, nr_seq_requisicao, ie_tipo_historico,
		 ds_historico, dt_atualizacao, nm_usuario,
		 dt_historico, ie_origem_historico)
	values (nextval('pls_requisicao_historico_seq'), nr_seq_requisicao_w, 'NR',
		 ds_historico_notif_p, clock_timestamp(), nm_usuario_p,
		 clock_timestamp(), 'M');
end if;

CALL pls_gravar_hist_notific_neg(nr_seq_notificacao_p, ds_historico_notif_p, nm_usuario_p, cd_estabelecimento_w);

commit;

end;
$body$
