CREATE OR REPLACE PROCEDURE pls_proposta_alterar_dados ( nr_seq_proposta_p bigint, dt_inicio_proposta_p timestamp, nm_usuario_p text) AS $body$
DECLARE


dt_inicio_proposta_w	timestamp;
ds_historico_w		varchar(255);


BEGIN

select	dt_inicio_proposta
into STRICT	dt_inicio_proposta_w
from	pls_proposta_adesao
where	nr_sequencia	= nr_seq_proposta_p;


update	pls_proposta_adesao
set	dt_inicio_proposta	= dt_inicio_proposta_p,
	nm_usuario		= nm_usuario_p,
	dt_atualizacao		= clock_timestamp()
where	nr_sequencia		= nr_seq_proposta_p;

ds_historico_w	:= 'Data da proposta alterada de '||dt_inicio_proposta_w||' para '||dt_inicio_proposta_p;

insert	into	pls_proposta_historico(	nr_sequencia, nr_seq_proposta, dt_historico,
		ds_historico, dt_atualizacao, nm_usuario,
		dt_atualizacao_nrec, nm_usuario_nrec, ie_origem_historico)
	values (	nextval('pls_proposta_historico_seq'), nr_seq_proposta_p, clock_timestamp(),
		ds_historico_w, clock_timestamp(), nm_usuario_p,
		clock_timestamp(), nm_usuario_p, 1);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
