CREATE OR REPLACE PROCEDURE pls_proposta_benef_indicacao ( nr_seq_proposta_p bigint, nr_seq_contrato_p bigint, ie_opcao_p text, ie_commit_p text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


/*ie_opcao_p:
	B - informações destinadas à tabela "Indicação do beneficiário"
	P -  informações destinadas à tabela "Indicação da proposta" */
nr_seq_segurado_w		bigint;
nr_seq_segurado_indic_w		bigint;
cd_pessoa_indicacao_w		varchar(255);

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_segurado
	where	nr_seq_contrato	= nr_seq_contrato_p;


BEGIN

if (ie_opcao_p = 'B') then
	open C01;
	loop
	fetch C01 into
		nr_seq_segurado_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		insert into	pls_indicacao_venda(	nr_sequencia, dt_atualizacao, nm_usuario,
				dt_atualizacao_nrec, nm_usuario_nrec, nr_seq_segurado,
				cd_estabelecimento)
		values (	nextval('pls_indicacao_venda_seq'), clock_timestamp(), nm_usuario_p,
				clock_timestamp(), nm_usuario_p, nr_seq_segurado_w,
				cd_estabelecimento_p);
		end;
	end loop;
	close C01;
elsif (ie_opcao_p = 'P') then
	select	a.nr_seq_segurado_indic,
		a.cd_pessoa_indicacao
	into STRICT	nr_seq_segurado_indic_w,
		cd_pessoa_indicacao_w
	from	pls_proposta_adesao	b,
		pls_comercial_cliente	a
	where	a.nr_sequencia = b.nr_seq_cliente
	and	b.nr_sequencia = nr_seq_proposta_p;

	if	((nr_seq_segurado_indic_w IS NOT NULL AND nr_seq_segurado_indic_w::text <> '') or (cd_pessoa_indicacao_w IS NOT NULL AND cd_pessoa_indicacao_w::text <> '')) then
		insert into	pls_indicacao_venda(	nr_sequencia, dt_atualizacao, nm_usuario,
				dt_atualizacao_nrec, nm_usuario_nrec, nr_seq_proposta, nr_seq_segurado,
				cd_pessoa_fisica, cd_estabelecimento)
		values (	nextval('pls_indicacao_venda_seq'), clock_timestamp(), nm_usuario_p,
				clock_timestamp(), nm_usuario_p, nr_seq_proposta_p, nr_seq_segurado_indic_w,
				cd_pessoa_indicacao_w, cd_estabelecimento_p);
	end if;
end if;

if (ie_commit_p = 'S') then
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
