CREATE OR REPLACE PROCEDURE pls_proposta_doc_contrato ( nr_seq_proposta_p bigint, nr_seq_contrato_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_contrato_doc_w		bigint;
qt_documentos_contrato_w	bigint;
nr_seq_documento_w		bigint;
cd_classificacao_w		varchar(20);
ds_dispositivo_w		varchar(255);
ie_gerar_titulo_w		varchar(1);
ie_quebra_pagina_w		varchar(1);
nr_seq_contrato_doc_texto_w	bigint;
nr_seq_doc_texto_w		bigint;
nr_seq_tipo_tema_w		pls_contrato_doc_texto.nr_seq_tipo_tema%type;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		cd_classificacao,
		ds_dispositivo,
		ie_imprimir_titulo,
		ie_quebra_pagina,
		nr_seq_tipo_tema
	from	pls_contrato_doc_texto
	where	nr_seq_documento	= nr_seq_contrato_doc_w;


BEGIN

select	max(nr_sequencia)
into STRICT	nr_seq_contrato_doc_w
from	pls_contrato_documento
where	nr_seq_proposta	= nr_seq_proposta_p;

select	count(*)
into STRICT	qt_documentos_contrato_w
from	pls_contrato		b,
	pls_contrato_documento	a
where	a.nr_seq_contrato	= b.nr_sequencia
and	b.nr_sequencia		= nr_seq_contrato_p
and	coalesce(b.dt_aprovacao::text, '') = '';

if (qt_documentos_contrato_w = 0) and (nr_seq_contrato_doc_w IS NOT NULL AND nr_seq_contrato_doc_w::text <> '') then
	
	select	nextval('pls_contrato_documento_seq')
	into STRICT	nr_seq_documento_w
	;
	
	insert into pls_contrato_documento(	nr_sequencia,dt_atualizacao,nm_usuario,dt_atualizacao_nrec,nm_usuario_nrec,
			nr_seq_contrato,nr_seq_instrumento,ds_documento)
		(SELECT	nr_seq_documento_w,clock_timestamp(),nm_usuario_p,clock_timestamp(),nm_usuario_p,
			nr_seq_contrato_p,nr_seq_instrumento,ds_documento
		from	pls_contrato_documento
		where	nr_sequencia	= nr_seq_contrato_doc_w);
	
	open C01;
	loop
	fetch C01 into
		nr_seq_contrato_doc_texto_w,
		cd_classificacao_w,
		ds_dispositivo_w,
		ie_gerar_titulo_w,
		ie_quebra_pagina_w,
		nr_seq_tipo_tema_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		
		select	nextval('pls_contrato_doc_texto_seq')
		into STRICT	nr_seq_doc_texto_w
		;
		
		insert into pls_contrato_doc_texto(	nr_sequencia,dt_atualizacao,nm_usuario,dt_atualizacao_nrec,nm_usuario_nrec,
				nr_seq_documento,cd_classificacao,ds_dispositivo,ie_converte_macro,ie_imprimir_titulo,
				ie_quebra_pagina, nr_seq_tipo_tema)
		values (	nr_seq_doc_texto_w,clock_timestamp(),nm_usuario_p,clock_timestamp(),nm_usuario_p,
				nr_seq_documento_w,cd_classificacao_w,ds_dispositivo_w,'N',ie_gerar_titulo_w,
				ie_quebra_pagina_w, nr_seq_tipo_tema_w);
		
		CALL COPIA_CAMPO_LONG_DE_PARA('PLS_CONTRATO_DOC_TEXTO','DS_TEMA','where nr_sequencia = :nr_sequencia','nr_sequencia='||nr_seq_contrato_doc_texto_w,
					'PLS_CONTRATO_DOC_TEXTO','DS_TEMA','where nr_sequencia = :nr_sequencia','nr_sequencia='||nr_seq_doc_texto_w);
		
		end;
	end loop;
	close C01;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
